
import type { DictionaryTerm } from '@/lib/types';

export const mockDictionaryTerms: DictionaryTerm[] = [
  { id: 'term1', term: 'Desenho Universal', definition: 'Concepção de produtos, ambientes, programas e serviços a serem utilizados por todas as pessoas, sem necessidade de adaptação ou projeto especializado.', category: 'Inclusão' },
  { id: 'term2', term: 'Neurodiversidade', definition: 'Conceito que descreve as variações naturais no cérebro humano em relação à sociabilidade, aprendizagem, atenção, humor e outras funções mentais.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term3', term: 'Tecnologia Assistiva', definition: 'Recursos e serviços que contribuem para proporcionar ou ampliar habilidades funcionais de pessoas com deficiência.', category: 'Tecnologia e Suporte' },
  { id: 'term4', term: 'CIF', definition: 'Classificação Internacional de Funcionalidade, Incapacidade e Saúde: um sistema de classificação da OMS que descreve a saúde e os estados relacionados à saúde.', category: 'Saúde e Classificação' },
  { id: 'term5', term: 'Estimulação Precoce', definition: 'Conjunto de intervenções terapêuticas e educacionais destinadas a crianças pequenas (0-6 anos) com risco ou diagnóstico de atrasos no desenvolvimento.', category: 'Intervenção' },
  { id: 'term6', term: 'Funções Executivas', definition: 'Conjunto de habilidades cognitivas que permitem o planejamento, organização, execução de tarefas, controle inibitório e flexibilidade mental.', category: 'Cognição' },
  { id: 'term7', term: 'Comunicação Aumentativa e Alternativa (CAA)', definition: 'Formas de comunicação (além da fala) usadas para expressar pensamentos, necessidades, desejos e ideias. Inclui gestos, pranchas de comunicação, dispositivos eletrônicos.', category: 'Comunicação' },
  { id: 'term8', term: 'Modelo Biopsicossocial', definition: 'Abordagem que considera fatores biológicos, psicológicos e sociais na compreensão da saúde e da doença, incluindo a deficiência.', category: 'Modelos Teóricos'},
  { id: 'term9', term: 'Desenvolvimento Diverso', definition: 'Padrão de desenvolvimento que difere do mais comum para uma determinada idade ou estágio, reconhecendo a variação como parte da experiência humana.', category: 'Desenvolvimento'},
  { id: 'term10', term: 'Paralisia Cerebral', definition: 'Grupo de desordens permanentes do desenvolvimento do movimento e postura, causando limitação da atividade, que são atribuídas a distúrbios não progressivos que ocorreram no cérebro em desenvolvimento fetal ou infantil.', category: 'Condições de Saúde'},
  { id: 'term11', term: 'Dislexia', definition: 'Transtorno específico de aprendizagem de origem neurobiológica, caracterizado por dificuldades no reconhecimento preciso e/ou fluente da palavra, na habilidade de decodificação e em soletração.', category: 'Aprendizagem'},
  { id: 'term12', term: 'TDAH', definition: 'Transtorno do Déficit de Atenção com Hiperatividade, um transtorno neurodesenvolvimental caracterizado por níveis prejudiciais de desatenção, desorganização e/ou hiperatividade-impulsividade.', category: 'Neurodesenvolvimento'},
  { id: 'term13', term: 'TEA', definition: 'Transtorno do Espectro Autista, uma condição do neurodesenvolvimento caracterizada por particularidades na comunicação e interação social e padrões restritos e repetitivos de comportamento, interesses ou atividades.', category: 'Neurodesenvolvimento'},
  { id: 'term14', term: 'Desenvolvimento Motor Grosso', definition: 'Habilidades que envolvem grandes grupos musculares para realizar movimentos como andar, pular, sentar. Também chamado de motor amplo.', category: 'Desenvolvimento Físico'},
  { id: 'term15', term: 'Desenvolvimento Motor Fino', definition: 'Habilidades que envolvem pequenos grupos musculares, especialmente das mãos e dedos, para movimentos precisos como escrever, desenhar, abotoar.', category: 'Desenvolvimento Físico'},
  { id: 'term16', term: 'Espinha Bífida', definition: 'Malformação congênita da coluna vertebral em que as meninges, a medula espinhal ou ambas não se fecham completamente durante o desenvolvimento fetal.', category: 'Condições de Saúde'},
  { id: 'term17', term: 'Distrofia Muscular', definition: 'Grupo de doenças genéticas progressivas que causam fraqueza e degeneração dos músculos esqueléticos.', category: 'Condições de Saúde'},
  { id: 'term18', term: 'Orientação e Mobilidade (O&M)', definition: 'Técnicas e estratégias ensinadas a pessoas com deficiência visual para permitir que elas se orientem e se movam de forma segura e independente no ambiente.', category: 'Suporte e Reabilitação'},
  { id: 'term19', term: 'Deficiência Intelectual', definition: 'Condição caracterizada por limitações significativas tanto no funcionamento intelectual (raciocínio, aprendizado, resolução de problemas) quanto no comportamento adaptativo, que cobre habilidades sociais e práticas cotidianas, com início durante o período de desenvolvimento.', category: 'Neurodesenvolvimento'},
  { id: 'term20', term: 'Apego Seguro', definition: 'Padrão de vínculo afetivo em que a criança usa o cuidador como uma base segura para explorar o mundo e como fonte de conforto em momentos de aflição.', category: 'Desenvolvimento Socioafetivo'},
  { id: 'term21', term: 'Barreiras Atitudinais', definition: 'Preconceitos, estigmas, estereótipos e discriminações que dificultam a participação social de pessoas com deficiência.', category: 'Inclusão'},
  { id: 'term22', term: 'Facilitadores Ambientais', definition: 'Modificações no ambiente físico ou social que promovem a participação e autonomia de pessoas com deficiência.', category: 'Inclusão'},
  { id: 'term23', term: 'Capacitismo', definition: 'Discriminação e preconceito social contra pessoas com deficiência, baseado na crença de que habilidades típicas são superiores e na subestimação das capacidades das pessoas com deficiência.', category: 'Inclusão'},
  { id: 'term24', term: 'Psicomotricidade', definition: 'Ciência que estuda o ser humano por meio do seu corpo em movimento, em relação ao seu mundo interno e externo, bem como suas possibilidades de perceber, atuar e se relacionar com os outros.', category: 'Desenvolvimento Físico' },
  { id: 'term25', term: 'Libras (Língua Brasileira de Sinais)', definition: 'Língua de sinais usada pela maioria das comunidades de surdos do Brasil, reconhecida legalmente como meio de comunicação e expressão.', category: 'Comunicação' },
  { id: 'term26', term: 'Adaptações Razoáveis', definition: 'Modificações e ajustes necessários e apropriados que não acarretem ônus desproporcional ou indevido, a fim de assegurar que as pessoas com deficiência possam gozar ou exercer, em igualdade de condições, todos os direitos humanos e liberdades fundamentais.', category: 'Inclusão' },
  { id: 'term27', term: 'Deficiência Auditiva', definition: 'Perda parcial ou total da capacidade de ouvir, que pode variar em grau e tipo.', category: 'Condições de Saúde' },
  { id: 'term28', term: 'Deficiência Visual', definition: 'Abrange a cegueira (perda total da visão) e a baixa visão (comprometimento significativo da função visual, mesmo após tratamento ou correção).', category: 'Condições de Saúde' },
  { id: 'term29', term: 'Baixa Visão (ou Visão Subnormal)', definition: 'Comprometimento da função visual que interfere nas tarefas diárias, mas que permite o uso da visão para planejamento e execução de tarefas.', category: 'Condições de Saúde' },
  { id: 'term30', term: 'Processamento Sensorial', definition: 'A forma como o sistema nervoso recebe, organiza e interpreta as informações dos sentidos (tato, paladar, olfato, visão, audição, propriocepção e vestibular) para produzir respostas adequadas.', category: 'Cognição' },
  { id: 'term31', term: 'Integração Sensorial', definition: 'Processo neurológico que organiza as sensações do próprio corpo e do ambiente, tornando possível o uso eficaz do corpo no ambiente. É também uma abordagem terapêutica para dificuldades de processamento sensorial.', category: 'Intervenção' },
  { id: 'term32', term: 'Acessibilidade Digital', definition: 'Prática de tornar websites, aplicativos e ferramentas digitais utilizáveis por todas as pessoas, independentemente de suas habilidades ou deficiências.', category: 'Tecnologia e Suporte' },
  { id: 'term33', term: 'Terapia Ocupacional', definition: 'Profissão da área da saúde que promove a autonomia de indivíduos com dificuldades físicas, sensoriais, sociais ou cognitivas, por meio do engajamento em atividades significativas (ocupações).', category: 'Intervenção' },
  { id: 'term34', term: 'Fonoaudiologia', definition: 'Área da saúde que trabalha com os diferentes aspectos da comunicação humana: linguagem oral e escrita, fala, voz, audição e funções responsáveis pela deglutição, respiração e mastigação.', category: 'Intervenção' },
  { id: 'term35', term: 'Discalculia', definition: 'Transtorno específico da aprendizagem que afeta a capacidade de uma pessoa para compreender e manipular números e conceitos matemáticos.', category: 'Aprendizagem' },
  { id: 'term36', term: 'Disgrafia', definition: 'Dificuldade na aquisição e desenvolvimento da habilidade da escrita, resultando em uma caligrafia ilegível ou em uma produção escrita muito lenta.', category: 'Aprendizagem' },
  { id: 'term37', term: 'Apraxia de Fala na Infância', definition: 'Transtorno motor da fala em que o cérebro tem dificuldade em planejar e coordenar os movimentos dos músculos necessários para produzir a fala.', category: 'Comunicação' },
  { id: 'term38', term: 'Síndrome de Down', definition: 'Condição genética causada pela presença de um cromossomo 21 extra, que leva a características físicas típicas e a um grau variável de deficiência intelectual.', category: 'Condições de Saúde' },
  { id: 'term39', term: 'Interseccionalidade', definition: 'Abordagem que analisa como diferentes formas de opressão (como capacitismo, racismo, sexismo) se cruzam e criam experiências únicas de discriminação e privilégio.', category: 'Modelos Teóricos' },
  { id: 'term40', term: 'Pessoa com Deficiência (PcD)', definition: 'Termo correto e atual para se referir a quem tem impedimentos de longo prazo de natureza física, mental, intelectual ou sensorial, que em interação com barreiras, podem obstruir sua participação plena na sociedade.', category: 'Linguagem' },
  { id: 'term41', term: 'Equoterapia', definition: 'Método terapêutico e educacional que utiliza o cavalo dentro de uma abordagem multidisciplinar, buscando o desenvolvimento biopsicossocial de pessoas com deficiência e/ou necessidades especiais.', category: 'Intervenção' },
  { id: 'term42', term: 'Desenho Instrucional Universal para a Aprendizagem (UDL)', definition: 'Estrutura educacional que guia o desenvolvimento de ambientes de aprendizagem flexíveis e que podem acomodar as diferenças individuais de aprendizagem.', category: 'Educação Inclusiva' },
  { id: 'term43', term: 'Sala de Recursos Multifuncionais (SRM)', definition: 'Espaço na escola onde se realiza o Atendimento Educacional Especializado (AEE), com materiais, equipamentos e profissionais para apoiar alunos com deficiência, transtornos globais do desenvolvimento e altas habilidades.', category: 'Educação Inclusiva' },
  { id: 'term44', term: 'Atendimento Educacional Especializado (AEE)', definition: 'Serviço da educação especial que identifica, elabora e organiza recursos pedagógicos e de acessibilidade, que eliminem as barreiras para a plena participação dos alunos, considerando suas necessidades específicas.', category: 'Educação Inclusiva' },
  { id: 'term45', term: 'Plano de Desenvolvimento Individualizado (PDI)', definition: 'Ferramenta de planejamento e acompanhamento do desenvolvimento de um aluno, com metas, estratégias e recursos adaptados às suas necessidades individuais.', category: 'Educação Inclusiva' },
  { id: 'term46', term: 'Comorbidade', definition: 'Ocorrência de duas ou mais doenças ou condições no mesmo indivíduo, como TDAH e ansiedade, ou TEA e deficiência intelectual.', category: 'Saúde e Classificação' },
  { id: 'term47', term: 'Ecolalia', definition: 'Repetição de frases ou palavras ouvidas. Pode ser imediata ou tardia e é comum em pessoas no espectro autista.', category: 'Comunicação' },
  { id: 'term48', term: 'Estereotipia (ou Stimming)', definition: 'Comportamentos motores repetitivos, como balançar o corpo, agitar as mãos ou emitir sons, frequentemente usados para autorregulação por pessoas neurodivergentes.', category: 'Comportamento' },
  { id: 'term49', term: 'Hiperfoco', definition: 'Estado de concentração intensa em um determinado assunto, atividade ou tarefa, comum em pessoas com TDAH ou TEA.', category: 'Cognição' },
  { id: 'term50', term: 'Sobrecarga Sensorial', definition: 'Ocorre quando o cérebro recebe mais estímulos sensoriais (visuais, auditivos, táteis) do que consegue processar, podendo levar a estresse, ansiedade ou colapso (meltdown).', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term51', term: 'Meltdown', definition: 'Resposta intensa e incontrolável a uma sobrecarga (sensorial ou emocional), que pode se manifestar como choro, gritos, agitação ou afastamento. Não é uma "birra".', category: 'Comportamento' },
  { id: 'term52', term: 'Shutdown', definition: 'Outra resposta à sobrecarga, mas internalizada. A pessoa pode ficar quieta, não responsiva, apática ou se retirar para um local isolado para se recuperar.', category: 'Comportamento' },
  { id: 'term53', term: 'Flexibilidade Cognitiva', definition: 'Habilidade de alternar entre diferentes tarefas ou formas de pensar, e de se adaptar a mudanças no ambiente ou nas regras.', category: 'Cognição' },
  { id: 'term54', term: 'Teoria da Mente', definition: 'Capacidade de compreender que outras pessoas têm seus próprios pensamentos, crenças, desejos e intenções, que são diferentes dos nossos.', category: 'Cognição' },
  { id: 'term55', term: 'Acessibilidade Atitudinal', definition: 'Eliminação de preconceitos, estigmas e estereótipos, promovendo atitudes de respeito e empatia em relação às pessoas com deficiência.', category: 'Inclusão' },
  { id: 'term56', term: 'Acessibilidade Comunicacional', definition: 'Ausência de barreiras na comunicação interpessoal, na escrita e na comunicação digital (Libras, legendas, audiodescrição, linguagem simples).', category: 'Inclusão' },
  { id: 'term57', term: 'Acessibilidade Metodológica', definition: 'Ausência de barreiras nos métodos e técnicas de estudo, trabalho, e atividades em geral, permitindo a participação de todos.', category: 'Inclusão' },
  { id: 'term58', term: 'Audiodescrição', definition: 'Recurso de acessibilidade que traduz imagens em palavras, permitindo que pessoas com deficiência visual compreendam conteúdos audiovisuais ou estáticos.', category: 'Tecnologia e Suporte' },
  { id: 'term59', term: 'Linguagem Simples', definition: 'Técnica de redação que torna a informação mais fácil de ler e entender, beneficiando a todos, especialmente pessoas com deficiência intelectual ou baixo letramento.', category: 'Comunicação' },
  { id: 'term60', term: 'Modelo Social da Deficiência', definition: 'Abordagem que vê a deficiência como resultado da interação entre a pessoa com impedimentos e as barreiras impostas pela sociedade, e não como um problema individual.', category: 'Modelos Teóricos' },
  { id: 'term61', term: 'Vida Independente', definition: 'Filosofia e movimento que defende o direito das pessoas com deficiência de terem controle sobre suas próprias vidas, com as mesmas escolhas e oportunidades que os demais.', category: 'Direitos' },
  { id: 'term62', term: 'Protagonismo', definition: 'Atuação ativa e central da pessoa com deficiência nas decisões que afetam sua própria vida e a comunidade.', category: 'Direitos' },
  { id: 'term63', term: 'Miopatia', definition: 'Doença que afeta a musculatura, causando fraqueza muscular, cãibras ou espasmos.', category: 'Condições de Saúde' },
  { id: 'term64', term: 'Amputação', definition: 'Remoção de uma extremidade do corpo (braço, perna) por cirurgia ou trauma.', category: 'Condições de Saúde' },
  { id: 'term65', term: 'Lesão Medular', definition: 'Dano na medula espinhal que resulta em perda de função, como mobilidade ou sensibilidade.', category: 'Condições de Saúde' },
  { id: 'term66', term: 'Órtese', definition: 'Dispositivo externo utilizado para modificar as características estruturais ou funcionais do sistema neuromusculoesquelético (ex: talas, coletes).', category: 'Tecnologia e Suporte' },
  { id: 'term67', term: 'Prótese', definition: 'Dispositivo artificial que substitui uma parte do corpo ausente, como um braço ou perna.', category: 'Tecnologia e Suporte' },
  { id: 'term68', term: 'Cão-guia', definition: 'Cão treinado para auxiliar pessoas com deficiência visual em sua locomoção e segurança.', category: 'Suporte e Reabilitação' },
  { id: 'term69', term: 'Habilidades Sociais', definition: 'Conjunto de comportamentos aprendidos que nos permitem interagir de forma eficaz e satisfatória com os outros (ex: iniciar conversas, expressar sentimentos, pedir ajuda).', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term70', term: 'Autorregulação', definition: 'Capacidade de gerenciar as próprias emoções, pensamentos e comportamentos de forma adaptativa em diferentes situações.', category: 'Cognição' },
  { id: 'term71', term: 'Síndrome de Tourette', definition: 'Distúrbio neuropsiquiátrico caracterizado por múltiplos tiques motores e pelo menos um tique vocal, que ocorrem por mais de um ano.', category: 'Neurodesenvolvimento' },
  { id: 'term72', term: 'Transtorno Opositivo Desafiador (TOD)', definition: 'Padrão persistente de humor raivoso/irritável, comportamento questionador/desafiador ou índole vingativa.', category: 'Comportamento' },
  { id: 'term73', term: 'Mutismo Seletivo', definition: 'Transtorno de ansiedade em que uma pessoa, geralmente uma criança, é incapaz de falar em determinadas situações sociais, embora fale em outros contextos.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term74', term: 'Inclusão Social', definition: 'Processo de garantir que todas as pessoas, independentemente de suas características, tenham acesso a oportunidades, recursos e participação na sociedade.', category: 'Inclusão' },
  { id: 'term75', term: 'Segregação', definition: 'Separação de pessoas com deficiência do convívio social geral, em instituições ou classes especiais. É o oposto da inclusão.', category: 'Inclusão' },
  { id: 'term76', term: 'Integração', definition: 'Modelo anterior à inclusão, em que a pessoa com deficiência se insere no ambiente comum, mas é ela quem deve se adaptar, sem que o ambiente seja modificado para recebê-la.', category: 'Inclusão' },
  { id: 'term77', term: 'Transtorno de Processamento Auditivo Central (TPAC)', definition: 'Dificuldade em processar e interpretar os sons, apesar de a audição estar intacta. O problema está em "o que o cérebro faz com o que o ouvido ouve".', category: 'Aprendizagem' },
  { id: 'term78', term: 'Memória de Trabalho', definition: 'Sistema de memória de curto prazo que permite manter e manipular informações temporariamente para realizar tarefas cognitivas complexas.', category: 'Cognição' },
  { id: 'term79', term: 'Pictogramas', definition: 'Símbolos gráficos que representam objetos, conceitos ou ações, usados em sistemas de comunicação alternativa para facilitar a compreensão.', category: 'Comunicação' },
  { id: 'term80', term: 'Acessibilidade Física', definition: 'Eliminação de barreiras arquitetônicas em edificações, espaços e equipamentos urbanos, permitindo o acesso e uso por todas as pessoas.', category: 'Inclusão' },
  { id: 'term81', term: 'Bengala Longa', definition: 'Ferramenta de orientação e mobilidade usada por pessoas com deficiência visual para detectar obstáculos e identificar referências no ambiente.', category: 'Suporte e Reabilitação' },
  { id: 'term82', term: 'Piso Tátil', definition: 'Sinalização no chão com relevo para fornecer orientação e alerta a pessoas com deficiência visual. Divide-se em piso de alerta e direcional.', category: 'Inclusão' },
  { id: 'term83', term: 'Marcadores Sociais da Diferença', definition: 'Características como gênero, raça, etnia, orientação sexual e deficiência, que são socialmente construídas e usadas para classificar e hierarquizar pessoas.', category: 'Modelos Teóricos' },
  { id: 'term84', term: 'Validação', definition: 'Ato de reconhecer e aceitar os sentimentos, experiências e percepções de outra pessoa como válidos e legítimos.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term85', term: 'Emprego Apoiado', definition: 'Metodologia que visa a inclusão de pessoas com deficiência significativa no mercado de trabalho competitivo, por meio de suporte contínuo.', category: 'Inclusão' },
  { id: 'term86', term: 'Nanismo', definition: 'Condição que resulta em uma estatura significativamente abaixo da média da população, causada por fatores genéticos ou médicos.', category: 'Condições de Saúde' },
  { id: 'term87', term: 'Ostomia', definition: 'Procedimento cirúrgico que cria uma abertura (estoma) para permitir a saída de fezes ou urina. A pessoa que passa por isso é chamada de ostomizada.', category: 'Condições de Saúde' },
  { id: 'term88', term: 'Deficiências Invisíveis', definition: 'Condições que não são aparentes fisicamente, como dor crônica, TDAH, autismo, transtornos de ansiedade, fibromialgia, entre outras.', category: 'Saúde e Classificação' },
  { id: 'term89', term: 'Cultura Surda', definition: 'Conjunto de valores, comportamentos, tradições e expressões artísticas compartilhados por comunidades de pessoas surdas, que utilizam a língua de sinais como principal meio de comunicação.', category: 'Inclusão' },
  { id: 'term90', term: 'Cognição Social', definition: 'Processos mentais envolvidos em perceber, interpretar e responder ao comportamento social dos outros.', category: 'Cognição' },
  { id: 'term91', term: 'Poda Neural', definition: 'Processo natural no desenvolvimento do cérebro em que conexões sinápticas extras são eliminadas, tornando o cérebro mais eficiente.', category: 'Neurodesenvolvimento' },
  { id: 'term92', term: 'Controle Inibitório', definition: 'Uma das funções executivas; a capacidade de controlar impulsos e respostas automáticas para escolher um comportamento mais apropriado.', category: 'Cognição' },
  { id: 'term93', term: 'Perseveração', definition: 'Repetição persistente de uma palavra, gesto ou ideia, mesmo quando o estímulo que o provocou já cessou. Pode ser um comportamento motor ou verbal.', category: 'Comportamento' },
  { id: 'term94', term: 'Generalização', definition: 'Capacidade de aplicar uma habilidade ou conhecimento aprendido em uma situação para outras situações ou contextos semelhantes.', category: 'Aprendizagem' },
  { id: 'term95', term: 'Curadoria de Conteúdo Acessível', definition: 'Prática de selecionar, organizar e compartilhar conteúdo digital garantindo que ele seja acessível para todas as pessoas.', category: 'Tecnologia e Suporte' },
  { id: 'term96', term: 'Letramento', definition: 'Não apenas a habilidade de ler e escrever, mas o uso social e a aplicação dessas habilidades nas diversas práticas sociais.', category: 'Educação Inclusiva' },
  { id: 'term97', term: 'Práxis', definition: 'Capacidade de conceber, planejar e executar uma sequência de ações motoras não habituais.', category: 'Cognição' },
  { id: 'term98', term: 'Propriocepção', definition: 'Sentido que informa ao cérebro sobre a posição e o movimento do corpo no espaço, sem precisar usar a visão.', category: 'Desenvolvimento Físico' },
  { id: 'term99', term: 'Sistema Vestibular', definition: 'Sistema sensorial localizado no ouvido interno que contribui para o equilíbrio e o senso de orientação espacial.', category: 'Desenvolvimento Físico' },
  { id: 'term100', term: 'Inclusão Radical', definition: 'Abordagem que busca ativamente identificar e desmantelar todas as barreiras à participação, promovendo um pertencimento profundo e autêntico para cada indivíduo.', category: 'Inclusão' },
  { id: 'term101', term: 'Lei Brasileira de Inclusão (LBI)', definition: 'Também conhecida como Estatuto da Pessoa com Deficiência (Lei nº 13.146/2015), destina-se a assegurar e a promover, em condições de igualdade, o exercício dos direitos e das liberdades fundamentais por pessoa com deficiência, visando à sua inclusão social e cidadania.', category: 'Direitos' },
  { id: 'term102', term: 'Convenção sobre os Direitos da Pessoa com Deficiência (ONU)', definition: 'Tratado internacional de direitos humanos da ONU que visa proteger os direitos e a dignidade das pessoas com deficiência. No Brasil, possui status de emenda constitucional.', category: 'Direitos' },
  { id: 'term103', term: 'Análise do Comportamento Aplicada (ABA)', definition: 'Ciência de aprendizagem e comportamento baseada em princípios do behaviorismo, frequentemente utilizada como intervenção para o ensino de novas habilidades e manejo de comportamentos desafiadores.', category: 'Intervenção' },
  { id: 'term104', term: 'PECS (Picture Exchange Communication System)', definition: 'Sistema de comunicação por troca de figuras, desenvolvido para ajudar pessoas com dificuldades de fala a iniciar a comunicação de forma funcional.', category: 'Comunicação' },
  { id: 'term105', term: 'Transtorno do Desenvolvimento da Coordenação (TDC)', definition: 'Também conhecido como Dispraxia, é uma condição neurodesenvolvimental que afeta a capacidade do cérebro de planejar e executar movimentos motores, resultando em dificuldades de coordenação.', category: 'Neurodesenvolvimento' },
  { id: 'term106', term: 'Hipo/Hipersensibilidade', definition: 'Refere-se a uma resposta diminuída (hipo) ou exagerada (hiper) a estímulos sensoriais, como toque, som, luz ou movimento.', category: 'Cognição' },
  { id: 'term107', term: 'Seletividade Alimentar', definition: 'Comportamento caracterizado pela ingestão de um repertório muito restrito de alimentos e forte resistência a experimentar novos. Comum em quadros de TEA e dificuldades de processamento sensorial.', category: 'Comportamento' },
  { id: 'term108', term: 'Comportamento Adaptativo', definition: 'Conjunto de habilidades conceituais, sociais e práticas que uma pessoa aprendeu e usa em sua vida diária para responder às demandas do ambiente.', category: 'Comportamento' },
  { id: 'term109', term: 'Mediação Escolar', definition: 'Apoio oferecido por um profissional para auxiliar o aluno com deficiência a acessar o currículo e a interagir socialmente no ambiente escolar.', category: 'Educação Inclusiva' },
  { id: 'term110', term: 'Síndrome de Rett', definition: 'Distúrbio do neurodesenvolvimento genético raro que afeta quase exclusivamente meninas e compromete o desenvolvimento após um período inicial normal, impactando fala, coordenação motora e uso das mãos.', category: 'Condições de Saúde' },
  { id: 'term111', term: 'Síndrome do X Frágil', definition: 'Condição genética hereditária que causa deficiência intelectual, problemas de aprendizagem e de comportamento, sendo mais comum em meninos.', category: 'Condições de Saúde' },
  { id: 'term112', term: 'Esclerose Múltipla (EM)', definition: 'Doença autoimune crônica que afeta o cérebro e a medula espinhal (sistema nervoso central), causando uma ampla gama de sintomas, incluindo problemas de visão, equilíbrio e força muscular.', category: 'Condições de Saúde' },
  { id: 'term113', term: 'Resiliência', definition: 'Capacidade de se adaptar e se recuperar de adversidades, traumas, tragédias ou estresse significativo.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term114', term: 'Leitor de Tela', definition: 'Software que converte o texto exibido na tela de um computador ou dispositivo móvel em fala ou em Braille, permitindo que pessoas com deficiência visual utilizem a tecnologia de forma autônoma.', category: 'Tecnologia e Suporte' },
  { id: 'term115', term: 'Domótica (Automação Residencial)', definition: 'Aplicação de tecnologia para automatizar tarefas domésticas, proporcionando maior autonomia, conforto e segurança para pessoas com mobilidade reduzida.', category: 'Tecnologia e Suporte' },
  { id: 'term116', term: 'Pareamento', definition: 'Técnica de ensino que envolve associar um estímulo neutro a um reforçador para que o estímulo neutro adquira as propriedades do reforçador.', category: 'Aprendizagem' },
  { id: 'term117', term: 'Pragmática', definition: 'Área da linguística que estuda o uso da linguagem em contexto social, incluindo regras de conversação, expressões não-literais e a adequação do discurso à situação.', category: 'Comunicação' },
  { id: 'term118', term: 'Prosódia', definition: 'Refere-se ao ritmo, entonação, melodia e pausas da fala, que transmitem significado e emoção. Pessoas no espectro autista podem apresentar uma prosódia atípica.', category: 'Comunicação' },
  { id: 'term119', term: 'Currículo Funcional Natural', definition: 'Abordagem curricular que foca no ensino de habilidades úteis e relevantes para a vida diária do aluno, em seus ambientes naturais (casa, escola, comunidade).', category: 'Educação Inclusiva' },
  { id: 'term120', term: 'Avaliação Psicopedagógica', definition: 'Processo que investiga as causas das dificuldades de aprendizagem de um indivíduo, considerando fatores cognitivos, emocionais, sociais e pedagógicos.', category: 'Aprendizagem' },
  { id: 'term121', term: 'Acessibilidade Programática', definition: 'Ausência de barreiras em políticas públicas, normas e regulamentos, garantindo que sejam inclusivos e não discriminatórios.', category: 'Inclusão' },
  { id: 'term122', term: 'Acessibilidade Instrumental', definition: 'Ausência de barreiras em instrumentos, utensílios e ferramentas de estudo, trabalho e lazer, incluindo adaptações para uso.', category: 'Inclusão' },
  { id: 'term123', term: 'Modelo de Direitos Humanos da Deficiência', definition: 'Framework que reconhece as pessoas com deficiência como sujeitos de direitos, enfatizando a dignidade, a autonomia e a participação plena na sociedade.', category: 'Modelos Teóricos' },
  { id: 'term124', term: 'Disartria', definition: 'Dificuldade na articulação das palavras causada por uma alteração no controle dos músculos da fala, resultando em uma fala imprecisa ou "arrastada".', category: 'Comunicação' },
  { id: 'term125', term: 'Ataxia', definition: 'Perda de coordenação dos movimentos voluntários, como andar ou pegar objetos, causada por danos ao cerebelo.', category: 'Condições de Saúde' },
  { id: 'term126', term: 'Gnosia', definition: 'Capacidade de reconhecer e interpretar estímulos sensoriais (objetos, sons, rostos) previamente aprendidos.', category: 'Cognição' },
  { id: 'term127', term: 'Agnosia', definition: 'Perda da capacidade de reconhecer estímulos sensoriais, na ausência de déficits sensoriais primários. Pode ser visual, auditiva, tátil, etc.', category: 'Cognição' },
  { id: 'term128', term: 'Empatia Cognitiva', definition: 'Capacidade de compreender a perspectiva e os sentimentos de outra pessoa (Teoria da Mente).', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term129', term: 'Empatia Afetiva', definition: 'Capacidade de sentir ou compartilhar as emoções de outra pessoa, respondendo de forma apropriada.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term130', term: 'BPC (Benefício de Prestação Continuada)', definition: 'Benefício assistencial no Brasil que garante um salário mínimo mensal à pessoa com deficiência de qualquer idade que comprove não possuir meios de prover a própria manutenção nem de tê-la provida por sua família.', category: 'Direitos' },
  { id: 'term131', term: 'Equidade', definition: 'Princípio de justiça que leva em conta as necessidades individuais para garantir que todos tenham oportunidades justas, oferecendo mais suporte a quem precisa mais.', category: 'Inclusão' },
  { id: 'term132', term: 'Fadiga Crônica', definition: 'Cansaço extremo e persistente que não melhora com o repouso e que interfere nas atividades diárias, comum em diversas condições de saúde.', category: 'Condições de Saúde' },
  { id: 'term133', term: 'Coerência Central Fraca', definition: 'Teoria cognitiva (associada ao autismo) que descreve uma tendência a focar em detalhes em detrimento da compreensão do quadro geral ou do contexto.', category: 'Modelos Teóricos' },
  { id: 'term134', term: 'Acomodação Sensorial', definition: 'Estratégias e ferramentas usadas para ajudar uma pessoa a lidar com suas sensibilidades, como fones de ouvido para ruído ou óculos escuros para luz.', category: 'Intervenção' },
  { id: 'term135', term: 'Planejamento Motor', definition: 'Capacidade de conceber, organizar e executar uma sequência de movimentos desconhecidos para completar uma tarefa.', category: 'Desenvolvimento Físico' },
  { id: 'term136', term: 'Regulação Emocional', definition: 'Capacidade de gerenciar e controlar as próprias respostas emocionais, ajustando sua intensidade e duração.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term137', term: 'Burnout Parental', definition: 'Estado de exaustão física, emocional e mental em pais/cuidadores, resultante do estresse crônico associado a cuidar de filhos, especialmente aqueles com demandas intensas.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term138', term: 'Suporte Intermitente', definition: 'Apoio necessário apenas em momentos específicos, de forma episódica (ex: durante uma transição de vida).', category: 'Suporte e Reabilitação' },
  { id: 'term139', term: 'Suporte Limitado', definition: 'Apoio consistente necessário por um tempo determinado, mas não contínuo (ex: treinamento para um novo emprego).', category: 'Suporte e Reabilitação' },
  { id: 'term140', term: 'Suporte Extensivo', definition: 'Apoio regular e diário em alguns ambientes (ex: na escola ou em casa) e sem limitação de tempo.', category: 'Suporte e Reabilitação' },
  { id: 'term141', term: 'Suporte Generalizado', definition: 'Apoio constante, de alta intensidade e necessário em múltiplos ambientes para a sobrevivência.', category: 'Suporte e Reabilitação' },
  { id: 'term142', term: 'Máscara Autista (Masking)', definition: 'Estratégia consciente ou inconsciente usada por pessoas autistas para camuflar suas características autênticas e imitar comportamentos neurotípicos, a fim de se encaixar socialmente.', category: 'Comportamento' },
  { id: 'term143', term: 'Alexitimia', definition: 'Dificuldade em identificar e descrever as próprias emoções. É uma condição comum em pessoas no espectro autista.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term144', term: 'Comportamento Disruptivo', definition: 'Ações que interrompem o fluxo de uma atividade ou o ambiente social, como agressão, gritos ou recusa em seguir instruções.', category: 'Comportamento' },
  { id: 'term145', term: 'Análise Funcional do Comportamento', definition: 'Processo para identificar a relação entre um comportamento e o ambiente, determinando o antecedente, o comportamento e a consequência (ABC) para entender sua função.', category: 'Intervenção' },
  { id: 'term146', term: 'Reforço Positivo', definition: 'Adição de um estímulo agradável após um comportamento, com o objetivo de aumentar a probabilidade de que esse comportamento ocorra novamente.', category: 'Aprendizagem' },
  { id: 'term147', term: 'Reforço Negativo', definition: 'Remoção de um estímulo aversivo após um comportamento, com o objetivo de aumentar a probabilidade de que esse comportamento ocorra novamente.', category: 'Aprendizagem' },
  { id: 'term148', term: 'Punição', definition: 'Consequência que diminui a probabilidade de um comportamento ocorrer no futuro. Pode ser positiva (adicionar algo aversivo) ou negativa (remover algo agradável).', category: 'Aprendizagem' },
  { id: 'term149', term: 'Extinção', definition: 'Processo de suspender o reforço que mantinha um comportamento, levando à sua diminuição gradual.', category: 'Aprendizagem' },
  { id: 'term150', term: 'Hidroterapia', definition: 'Uso da água como meio terapêutico para reabilitação física, aproveitando suas propriedades físicas como flutuação e pressão hidrostática.', category: 'Intervenção' },
  { id: 'term151', term: 'Espectro', definition: 'Termo usado para descrever um conjunto de condições que compartilham características, mas variam em tipo e gravidade (ex: Transtorno do Espectro Autista).', category: 'Saúde e Classificação' },
  { id: 'term152', term: 'Acessibilidade em Comunicações', definition: 'Garantir que a comunicação (websites, documentos, vídeos) seja acessível, utilizando legendas, audiodescrição, Libras, linguagem simples e formatos alternativos.', category: 'Tecnologia e Suporte' },
  { id: 'term153', term: 'Dica (Prompt)', definition: 'Um estímulo adicional usado para aumentar a chance de uma resposta correta durante o ensino. Pode ser verbal, gestual, físico, visual ou posicional.', category: 'Intervenção' },
  { id: 'term154', term: 'Esmaecimento (Fading)', definition: 'Retirada gradual das dicas (prompts) à medida que o aprendiz começa a demonstrar a habilidade de forma independente.', category: 'Intervenção' },
  { id: 'term155', term: 'Cadeia de Comportamentos (Chaining)', definition: 'Técnica para ensinar tarefas complexas, quebrando-as em pequenos passos e ensinando-os em sequência.', category: 'Intervenção' },
  { id: 'term156', term: 'Economia de Fichas', definition: 'Sistema de reforço em que um indivíduo ganha fichas (tokens) por comportamentos desejados, que podem ser trocadas posteriormente por recompensas maiores.', category: 'Intervenção' },
  { id: 'term157', term: 'Fibromialgia', definition: 'Síndrome crônica caracterizada por dor musculoesquelética generalizada, fadiga, distúrbios do sono e sensibilidade em pontos específicos.', category: 'Condições de Saúde' },
  { id: 'term158', term: 'Dor Crônica', definition: 'Dor que persiste por mais de três meses, podendo continuar mesmo após a lesão inicial ter sido curada.', category: 'Condições de Saúde' },
  { id: 'term159', term: 'Comunicação Suplementar e Alternativa (CSA)', definition: 'Termo que engloba a Comunicação Aumentativa e Alternativa (CAA), usado para enfatizar que essas formas de comunicação complementam (suplementam) a fala existente.', category: 'Comunicação' },
  { id: 'term160', term: 'Inibição da Resposta', definition: 'Capacidade de suprimir uma resposta motora ou comportamental que é inadequada ou irrelevante para a tarefa atual.', category: 'Cognição' },
  { id: 'term161', term: 'Alfabetização Emergente', definition: 'Conhecimentos e habilidades que são precursores da leitura e da escrita, como o conhecimento das letras, a consciência fonológica e o manuseio de livros.', category: 'Aprendizagem' },
  { id: 'term162', term: 'Consciência Fonológica', definition: 'Habilidade de reconhecer e manipular os sons da fala, como identificar rimas, sílabas e fonemas.', category: 'Aprendizagem' },
  { id: 'term163', term: 'Intervenção Baseada em Evidências', definition: 'Prática profissional que se baseia na integração da melhor evidência de pesquisa com a experiência clínica e os valores do paciente.', category: 'Intervenção' },
  { id: 'term164', term: 'Brincar Simbólico', definition: 'Capacidade de usar objetos, ações ou ideias para representar outros objetos, ações ou ideias (ex: usar uma banana como telefone).', category: 'Desenvolvimento' },
  { id: 'term165', term: 'Atenção Compartilhada', definition: 'Habilidade de compartilhar um foco de atenção com outra pessoa sobre um objeto ou evento, usando o olhar e gestos.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term166', term: 'Mentalização', definition: 'Capacidade de entender o próprio comportamento e o dos outros em termos de estados mentais (sentimentos, desejos, crenças).', category: 'Cognição' },
  { id: 'term167', term: 'Rigidez Cognitiva', definition: 'Dificuldade em mudar de pensamento ou de adaptar o comportamento a novas situações, preferindo rotinas e regras fixas.', category: 'Cognição' },
  { id: 'term168', term: 'Pensamento Concreto', definition: 'Estilo de pensamento focado no "aqui e agora" e em significados literais, com dificuldade em abstrair e entender conceitos complexos.', category: 'Cognição' },
  { id: 'term169', term: 'Tônus Muscular', definition: 'Estado de tensão ou contração parcial de um músculo em repouso. Pode ser baixo (hipotonia) ou alto (hipertonia).', category: 'Desenvolvimento Físico' },
  { id: 'term170', term: 'Hipotonia', definition: 'Tônus muscular diminuído, resultando em "flacidez" muscular e dificuldades com postura e movimento.', category: 'Desenvolvimento Físico' },
  { id: 'term171', term: 'Hipertonia', definition: 'Tônus muscular aumentado, resultando em rigidez muscular e dificuldade de movimento.', category: 'Desenvolvimento Físico' },
  { id: 'term172', term: 'Movimento de base (Grassroots)', definition: 'Movimento social que surge a partir da própria comunidade, impulsionado por suas necessidades e membros, como o movimento pelos direitos das pessoas com deficiência.', category: 'Direitos' },
  { id: 'term173', term: 'Nada Sobre Nós Sem Nós', definition: 'Slogan global que comunica a ideia de que nenhuma política deve ser decidida por qualquer representante sem a participação plena e direta dos membros do grupo afetado.', category: 'Direitos' },
  { id: 'term174', term: 'Defesa de Direitos (Advocacy)', definition: 'Ato de apoiar ativamente uma causa ou política para promover os direitos e interesses de um indivíduo ou grupo.', category: 'Direitos' },
  { id: 'term175', term: 'Autodefensoria', definition: 'Movimento e prática em que as pessoas com deficiência, especialmente intelectual, se tornam porta-vozes de suas próprias causas, defendendo seus direitos e escolhas.', category: 'Direitos' },
  { id: 'term176', term: 'Design Inclusivo', definition: 'Processo de design que considera a diversidade humana em termos de habilidade, linguagem, cultura, gênero e idade, criando produtos e serviços que são utilizáveis pelo maior número possível de pessoas.', category: 'Inclusão' },
  { id: 'term177', term: 'Acessibilidade Web (WCAG)', definition: 'Diretrizes de Acessibilidade para Conteúdo Web, um conjunto de recomendações internacionais para tornar o conteúdo da web mais acessível.', category: 'Tecnologia e Suporte' },
  { id: 'term178', term: 'Interdependência', definition: 'Reconhecimento de que todos os seres humanos são mutuamente dependentes, valorizando as relações de cuidado e suporte como parte da condição humana.', category: 'Modelos Teóricos' },
  { id: 'term179', term: 'Mapeamento Corporal', definition: 'Consciência sobre as partes do próprio corpo, suas posições e como elas se movem no espaço.', category: 'Desenvolvimento Físico' },
  { id: 'term180', term: 'Lateralidade', definition: 'Predominância funcional de um lado do corpo sobre o outro (ex: ser destro ou canhoto), que é fundamental para a orientação espacial.', category: 'Desenvolvimento Físico' },
  { id: 'term181', term: 'Estigma', definition: 'Marca de desaprovação social que marginaliza uma pessoa por causa de uma característica particular, como uma deficiência.', category: 'Inclusão' },
  { id: 'term182', term: 'Identidade Afirmativa (Identity-First Language)', definition: 'Linguagem que coloca a característica da deficiência como parte da identidade da pessoa (ex: "pessoa autista" em vez de "pessoa com autismo"). A preferência varia individualmente.', category: 'Linguagem' },
  { id: 'term183', term: 'Pessoa em Primeiro Lugar (Person-First Language)', definition: 'Linguagem que coloca a pessoa antes da deficiência (ex: "pessoa com deficiência"). É a linguagem oficialmente recomendada pela LBI.', category: 'Linguagem' },
  { id: 'term184', term: 'Abordagem Centrada na Família', definition: 'Modelo de prestação de serviços que reconhece a família como constante na vida da criança e a envolve como parceira nas decisões sobre o cuidado e a intervenção.', category: 'Intervenção' },
  { id: 'term185', term: 'Modelo Denver de Intervenção Precoce (ESDM)', definition: 'Abordagem de intervenção comportamental baseada no brincar para crianças pequenas com autismo, focada no desenvolvimento social e da comunicação.', category: 'Intervenção' },
  { id: 'term186', term: 'Sonoterapia', definition: 'Uso terapêutico de sons e música para promover relaxamento, bem-estar e tratar problemas de saúde física e mental.', category: 'Intervenção' },
  { id: 'term187', term: 'Cromoterapia', definition: 'Prática terapêutica que utiliza cores para equilibrar o corpo e a mente, embora careça de comprovação científica robusta.', category: 'Intervenção' },
  { id: 'term188', term: 'Desempenho Ocupacional', definition: 'Capacidade de uma pessoa realizar as atividades que compõem suas ocupações diárias (autocuidado, lazer, trabalho).', category: 'Intervenção' },
  { id: 'term189', term: 'Tecnologia Social', definition: 'Produtos, técnicas ou metodologias replicáveis, desenvolvidas na interação com a comunidade e que representam soluções eficazes de transformação social.', category: 'Tecnologia e Suporte' },
  { id: 'term190', term: 'Deficiência Psicossocial', definition: 'Deficiência que surge de transtornos mentais e que, em interação com barreiras sociais, impede a participação plena na sociedade.', category: 'Saúde e Classificação' },
  { id: 'term191', term: 'Despatologização', definition: 'Movimento que busca retirar certas condições (como a transexualidade e a neurodiversidade) do campo da patologia, reconhecendo-as como manifestações da diversidade humana.', category: 'Modelos Teóricos' },
  { id: 'term192', term: 'Sobrediagnóstico', definition: 'Diagnosticar uma condição que, na verdade, não existe, ou que é tão branda que não traria prejuízos, transformando problemas comuns em transtornos médicos.', category: 'Saúde e Classificação' },
  { id: 'term193', term: 'Inteligências Múltiplas', definition: 'Teoria de Howard Gardner que propõe a existência de diferentes tipos de inteligência (lógico-matemática, linguística, espacial, musical, corporal-cinestésica, interpessoal, intrapessoal, naturalista).', category: 'Modelos Teóricos' },
  { id: 'term194', term: 'Microagressões', definition: 'Comentários ou ações sutis, muitas vezes não intencionais, que expressam um preconceito em relação a um grupo marginalizado (ex: "Você nem parece deficiente").', category: 'Inclusão' },
  { id: 'term195', term: 'Intervenção Precoce Centrada na Criança', definition: 'Abordagem terapêutica que segue os interesses e a liderança da criança durante as sessões para promover o desenvolvimento em um contexto natural e motivador.', category: 'Intervenção' },
  { id: 'term196', term: 'Vulnerabilidade Social', definition: 'Condição de indivíduos ou grupos que estão em situação de fragilidade e maior exposição a riscos e violações de direitos.', category: 'Inclusão' },
  { id: 'term197', term: 'Inerência da Deficiência', definition: 'Ideia de que a deficiência é uma parte natural e esperada da diversidade e da experiência humana.', category: 'Modelos Teóricos' },
  { id: 'term198', term: 'Escuta Ativa', definition: 'Técnica de comunicação que envolve focar completamente no que o outro está dizendo, compreendendo a mensagem e respondendo de forma reflexiva, sem julgamentos.', category: 'Comunicação' },
  { id: 'term199', term: 'Teoria do Apego', definition: 'Teoria de John Bowlby que descreve a importância do vínculo afetivo entre a criança e seu cuidador principal para o desenvolvimento socioemocional saudável.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term200', term: 'Pertencimento', definition: 'Sentimento de ser aceito, valorizado e incluído como membro de um grupo ou comunidade, sendo um elemento crucial para o bem-estar e a inclusão social.', category: 'Inclusão' },
  { id: 'term201', term: 'Tomada de Decisão Apoiada', definition: 'Processo pelo qual a pessoa com deficiência elege pelo menos duas pessoas de sua confiança para lhe prestar apoio na tomada de decisões sobre atos da vida civil, fornecendo-lhe informações e elementos necessários para que possa exercer sua capacidade legal.', category: 'Direitos' },
  { id: 'term202', term: 'Curatela', definition: 'Medida judicial extraordinária aplicada a pessoas que, por causa transitória ou permanente, não podem exprimir sua vontade. A pessoa sob curatela (curatelado) tem seus atos civis representados por um curador. A LBI prioriza a Tomada de Decisão Apoiada.', category: 'Direitos' },
  { id: 'term203', term: 'Capacidade Legal', definition: 'Aptidão de uma pessoa para adquirir direitos e exercer, por si mesma, os atos da vida civil. A Lei Brasileira de Inclusão assegura que a deficiência não afeta a plena capacidade civil da pessoa.', category: 'Direitos' },
  { id: 'term204', term: 'Coensino (Co-teaching)', definition: 'Modelo de ensino em que dois ou mais professores (geralmente um de educação geral e um de educação especial) compartilham a responsabilidade de planejar, instruir e avaliar um grupo heterogêneo de alunos na mesma sala de aula.', category: 'Educação Inclusiva' },
  { id: 'term205', term: 'Tutoria de Pares', definition: 'Estratégia de aprendizagem cooperativa em que alunos mais proficientes em uma habilidade ajudam outros alunos a aprender, beneficiando tanto o tutor quanto o tutorado.', category: 'Educação Inclusiva' },
  { id: 'term206', term: 'Interocepção', definition: 'Oitavo sentido; a percepção do estado interno do corpo, como fome, sede, dor, frequência cardíaca e necessidade de ir ao banheiro. Dificuldades na interocepção podem afetar a regulação emocional.', category: 'Cognição' },
  { id: 'term207', term: 'Neuroplasticidade', definition: 'Capacidade do cérebro de se reorganizar e formar novas conexões neurais ao longo da vida, em resposta à aprendizagem, experiência ou após uma lesão.', category: 'Neurodesenvolvimento' },
  { id: 'term208', term: 'Epigenética', definition: 'Estudo de como fatores ambientais e comportamentais podem causar mudanças na forma como os genes funcionam, sem alterar a sequência de DNA. É relevante para entender a interação entre predisposição genética e ambiente no desenvolvimento.', category: 'Saúde e Classificação' },
  { id: 'term209', term: 'Carga Alostática', definition: 'O desgaste acumulado no corpo resultante do estresse crônico. Níveis elevados de estresse, como os vividos por minorias estigmatizadas, podem levar a uma alta carga alostática e a problemas de saúde.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term210', term: 'DIR/Floortime', definition: 'Modelo de intervenção baseado no desenvolvimento, nas diferenças individuais e nas relações (Developmental, Individual-Difference, Relationship-Based). A terapia (Floortime) envolve seguir a liderança da criança no brincar para promover o desenvolvimento.', category: 'Intervenção' },
  { id: 'term211', term: 'RDI (Relationship Development Intervention)', definition: 'Programa de intervenção focado no desenvolvimento da inteligência emocional e da conexão social, ensinando habilidades de relacionamento dinâmico e flexível.', category: 'Intervenção' },
  { id: 'term212', term: 'Gamificação', definition: 'Uso de elementos e mecânicas de jogos (pontos, fases, medalhas) em contextos não lúdicos, como terapia ou educação, para aumentar o engajamento e a motivação.', category: 'Tecnologia e Suporte' },
  { id: 'term213', term: 'Classe Hospitalar', definition: 'Atendimento educacional oferecido a crianças e adolescentes hospitalizados, garantindo a continuidade de sua escolarização durante o tratamento de saúde.', category: 'Educação Inclusiva' },
  { id: 'term214', term: 'Avaliação Formativa', definition: 'Processo de avaliação contínua durante a aprendizagem, cujo objetivo é monitorar o progresso do aluno e fornecer feedback para ajustar o ensino, em vez de apenas classificar o resultado final.', category: 'Educação Inclusiva' },
  { id: 'term215', term: 'Síndrome de Asperger', definition: 'Termo diagnóstico anteriormente usado, agora integrado ao Transtorno do Espectro Autista (TEA) no DSM-5. Caracterizava-se por dificuldades na interação social e interesses restritos, sem atraso significativo na linguagem ou no desenvolvimento cognitivo.', category: 'Saúde e Classificação' },
  { id: 'term216', term: 'Disforia da Rejeição Sensível (RSD)', definition: 'Dor emocional extrema ou sensibilidade sentida em resposta à percepção de rejeição, crítica ou fracasso. É um conceito fortemente associado ao TDAH.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term217', term: 'Sobrecarga Cognitiva', definition: 'Situação em que a quantidade de informação a ser processada excede a capacidade da memória de trabalho, levando a erros e à incapacidade de tomar decisões.', category: 'Cognição' },
  { id: 'term218', term: 'Validação Social', definition: 'Processo de determinar se um comportamento é "correto" ou "apropriado" observando o que as outras pessoas estão fazendo.', category: 'Comportamento' },
  { id: 'term219', term: 'Capital Social', definition: 'Recursos disponíveis para indivíduos através de suas redes de relacionamentos, como confiança, cooperação e apoio mútuo.', category: 'Inclusão' },
  { id: 'term220', term: 'Burnout Autista', definition: 'Estado de exaustão física, mental e emocional, perda de habilidades e aumento da sensibilidade a estímulos, resultante do estresse cumulativo de tentar corresponder às demandas neurotípicas.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term221', term: 'Acessibilidade Natural', definition: 'Criação de ambientes e produtos que são inerentemente acessíveis a todos desde o início, sem a necessidade de adaptações posteriores.', category: 'Inclusão' },
  { id: 'term222', term: 'Justiça Social', definition: 'Princípio que defende uma distribuição justa de riquezas, oportunidades e privilégios dentro de uma sociedade, combatendo as desigualdades.', category: 'Direitos' },
  { id: 'term223', term: 'Fenótipo Comportamental', definition: 'Padrão característico de comportamentos, desenvolvimento cognitivo e social associado a uma condição genética específica (ex: a sociabilidade na Síndrome de Williams).', category: 'Comportamento' },
  { id: 'term224', term: 'Manutenção de Habilidades', definition: 'Capacidade de continuar a executar uma habilidade aprendida ao longo do tempo, mesmo depois que a instrução direta tenha terminado.', category: 'Aprendizagem' },
  { id: 'term225', term: 'Síndrome de Williams', definition: 'Condição genética rara caracterizada por problemas médicos, desafios de desenvolvimento, mas também por uma personalidade notavelmente sociável e empática e afinidade com a música.', category: 'Condições de Saúde' },
  { id: 'term226', term: 'Discurso Interno (Self-Talk)', definition: 'A conversa que temos dentro de nossa própria mente. É fundamental para a organização do pensamento, regulação emocional e planejamento.', category: 'Cognição' },
  { id: 'term227', term: 'Processamento de Linguagem Natural (PLN)', definition: 'Área da inteligência artificial que permite que computadores compreendam, interpretem e gerem linguagem humana. Base para tecnologias assistivas como assistentes de voz.', category: 'Tecnologia e Suporte' },
  { id: 'term228', term: 'Comunicação Não-Verbal (CNV)', definition: 'Comunicação através de linguagem corporal, gestos, expressões faciais, postura e contato visual, que complementa ou substitui a comunicação verbal.', category: 'Comunicação' },
  { id: 'term229', term: 'Transtorno de Acumulação', definition: 'Dificuldade persistente em descartar ou se separar de posses, independentemente de seu valor real, devido a uma necessidade percebida de guardá-las.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term230', term: 'Apatia', definition: 'Falta de sentimento, emoção, interesse ou preocupação. Pode ser um sintoma de várias condições neurológicas ou psiquiátricas.', category: 'Comportamento' },
  { id: 'term231', term: 'Aprendizagem Baseada em Projetos (ABP)', definition: 'Metodologia de ensino em que os alunos ganham conhecimento e habilidades trabalhando por um período prolongado para investigar e responder a uma questão, problema ou desafio autêntico.', category: 'Educação Inclusiva' },
  { id: 'term232', term: 'Dissonância Cognitiva', definition: 'O desconforto mental experimentado por uma pessoa que mantém duas ou mais crenças, ideias ou valores contraditórios.', category: 'Cognição' },
  { id: 'term233', term: 'Andragogia', definition: 'A arte e a ciência de ajudar adultos a aprender, em contraste com a pedagogia (ensino de crianças). Considera a experiência e a autonomia do aprendiz adulto.', category: 'Aprendizagem' },
  { id: 'term234', term: 'Intervenção em Crise', definition: 'Apoio psicológico imediato e de curto prazo oferecido a indivíduos que experimentaram um evento traumático para ajudar a restaurar o equilíbrio e a funcionalidade.', category: 'Intervenção' },
  { id: 'term235', term: 'Psicoeducação', definition: 'Processo de fornecer educação e informação a quem procura serviços de saúde mental, como pacientes e suas famílias, para ajudá-los a compreender e lidar com a condição.', category: 'Intervenção' },
  { id: 'term236', term: 'Alfabetização Digital', definition: 'Capacidade de usar tecnologias digitais, ferramentas de comunicação e redes para localizar, avaliar, usar e criar informações.', category: 'Tecnologia e Suporte' },
  { id: 'term237', term: 'Divisão Digital', definition: 'A lacuna entre aqueles que têm acesso e sabem usar as tecnologias de informação e comunicação e aqueles que não têm.', category: 'Inclusão' },
  { id: 'term238', term: 'Wayfinding (Navegação)', definition: 'Processo cognitivo de se orientar e se deslocar no espaço físico. Sistemas de wayfinding (sinalização, mapas táteis) são cruciais para a acessibilidade.', category: 'Inclusão' },
  { id: 'term239', term: 'Esquema Corporal', definition: 'Representação interna e inconsciente do corpo em movimento no espaço, que permite a automação dos gestos.', category: 'Desenvolvimento Físico' },
  { id: 'term240', term: 'Imagem Corporal', definition: 'Representação mental consciente e subjetiva do próprio corpo, incluindo percepções, pensamentos e sentimentos sobre ele.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term241', term: 'Transtorno Dismórfico Corporal (TDC)', definition: 'Transtorno mental em que a pessoa tem uma preocupação obsessiva com um defeito percebido em sua aparência física.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term242', term: 'Mindfulness (Atenção Plena)', definition: 'Prática de prestar atenção intencionalmente ao momento presente, sem julgamento, o que pode ajudar na regulação emocional e na redução do estresse.', category: 'Intervenção' },
  { id: 'term243', term: 'Paradesporto', definition: 'Esportes praticados por pessoas com deficiência, adaptados para permitir a competição. Inclui os Jogos Paralímpicos.', category: 'Inclusão' },
  { id: 'term244', term: 'Inércia do TDAH', definition: 'Dificuldade em iniciar tarefas (inércia de ativação) e em parar uma tarefa uma vez que se está engajado nela (inércia de desativação), mesmo que seja para algo importante.', category: 'Comportamento' },
  { id: 'term245', term: 'Diretivas Antecipadas de Vontade', definition: 'Documento em que uma pessoa, com discernimento, expressa seus desejos sobre os cuidados e tratamentos de saúde que quer ou não receber caso fique incapaz de se manifestar.', category: 'Direitos' },
  { id: 'term246', term: 'Síndrome de Irlen', definition: 'Distúrbio do processamento visual que afeta a forma como o cérebro interpreta a luz, podendo causar dificuldades de leitura, dores de cabeça e sensibilidade à luz. O uso de filtros coloridos pode ajudar.', category: 'Aprendizagem' },
  { id: 'term247', term: 'Inveja do Corpo (Body Doubling)', definition: 'Estratégia de produtividade, útil para pessoas com TDAH, em que ter outra pessoa presente (fisicamente ou virtualmente) ajuda a iniciar e manter o foco em uma tarefa.', category: 'Suporte e Reabilitação' },
  { id: 'term248', term: 'Cognição Incorporada (Embodied Cognition)', definition: 'Teoria de que os processos cognitivos são profundamente enraizados nas interações do corpo com o mundo, sugerindo que a mente não está apenas ligada ao corpo, mas que o corpo influencia o pensamento.', category: 'Modelos Teóricos' },
  { id: 'term249', term: 'Ambiente Menos Restritivo (LRE)', definition: 'Princípio educacional que determina que um aluno com deficiência deve ser educado na classe comum com seus pares sem deficiência, na máxima extensão apropriada.', category: 'Educação Inclusiva' },
  { id: 'term250', term: 'Design Thinking', definition: 'Abordagem para a resolução de problemas que é centrada no ser humano, colaborativa e iterativa. Usada para criar soluções inovadoras, incluindo para acessibilidade.', category: 'Modelos Teóricos' },
  { id: 'term251', term: 'Interoperabilidade', definition: 'Capacidade de diferentes sistemas e tecnologias de informação se comunicarem e trocarem dados de forma eficaz. Crucial para tecnologias assistivas integradas.', category: 'Tecnologia e Suporte' },
  { id: 'term252', term: 'Transtorno de Escoriação (Skin-Picking)', definition: 'Impulso recorrente de beliscar a própria pele, resultando em lesões. É um tipo de comportamento repetitivo focado no corpo.', category: 'Comportamento' },
  { id: 'term253', term: 'Tricotilomania', definition: 'Transtorno caracterizado pelo impulso recorrente de arrancar o próprio cabelo, levando à sua perda.', category: 'Comportamento' },
  { id: 'term254', term: 'Movimento Maker', definition: 'Cultura que representa uma extensão tecnológica do "faça você mesmo" (DIY). Permite a criação de próteses e tecnologias assistivas personalizadas e de baixo custo com impressão 3D.', category: 'Tecnologia e Suporte' },
  { id: 'term255', term: 'Parentalidade Atípica', definition: 'Refere-se à experiência de criar filhos que têm um desenvolvimento atípico, com seus desafios e alegrias únicos.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term256', term: 'Luto Parental', definition: 'Processo de luto que pais podem vivenciar ao receberem um diagnóstico de deficiência para o filho, relacionado à perda do "filho idealizado". É um sentimento complexo e não um sinal de rejeição.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term257', term: 'Aliado', definition: 'Pessoa que não pertence a um grupo marginalizado, mas que apoia ativamente seus direitos e luta contra a discriminação que sofrem.', category: 'Inclusão' },
  { id: 'term258', term: 'Viés Inconsciente', definition: 'Atitudes ou estereótipos que afetam nossa compreensão, ações e decisões de forma inconsciente, podendo levar a julgamentos e comportamentos discriminatórios.', category: 'Inclusão' },
  { id: 'term259', term: 'Ergonomia', definition: 'Ciência que estuda a adaptação do trabalho, dos equipamentos e do ambiente às características do ser humano, visando segurança e conforto. Essencial para criar postos de trabalho acessíveis.', category: 'Inclusão' },
  { id: 'term260', term: 'Carga Mental', definition: 'O trabalho invisível de gerenciar informações, planejar, organizar e tomar decisões, tanto na vida pessoal quanto profissional. Frequentemente desproporcional para mulheres e cuidadores.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term261', term: 'Modelo Afirmativo', definition: 'Modelo que celebra a identidade da deficiência como uma forma de diversidade humana, rejeitando a ideia de que a deficiência é inerentemente negativa ou algo a ser "consertado".', category: 'Modelos Teóricos' },
  { id: 'term262', term: 'Teoria da Carga Cognitiva', definition: 'Teoria de aprendizagem que sugere que o design instrucional deve evitar sobrecarregar a memória de trabalho do aluno para facilitar o processamento da informação.', category: 'Aprendizagem' },
  { id: 'term263', term: 'Literacia em Saúde', definition: 'Capacidade de um indivíduo obter, processar e compreender informações básicas de saúde e serviços necessários para tomar decisões de saúde apropriadas.', category: 'Saúde e Classificação' },
  { id: 'term264', term: 'Coping (Enfrentamento)', definition: 'Estratégias cognitivas e comportamentais usadas para lidar com o estresse ou situações difíceis.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term265', term: 'Telemedicina / Telessaúde', definition: 'Uso de tecnologias de comunicação para fornecer cuidados de saúde, consultas e informações à distância, aumentando o acesso para pessoas com mobilidade reduzida.', category: 'Tecnologia e Suporte' },
  { id: 'term266', term: 'Comunicação mediada por tecnologia', definition: 'Qualquer forma de comunicação que utiliza dispositivos eletrônicos como intermediários, desde e-mails até sistemas de CAA de alta tecnologia.', category: 'Comunicação' },
  { id: 'term267', term: 'Defensividade Tátil', definition: 'Hipersensibilidade ao toque, fazendo com que toques leves, certas texturas de roupas ou alimentos sejam percebidos como aversivos ou dolorosos.', category: 'Cognição' },
  { id: 'term268', term: 'Busca Sensorial', definition: 'Comportamento de procurar ativamente por estímulos sensoriais intensos (movimento, pressão, sons altos) para satisfazer uma necessidade neurológica.', category: 'Comportamento' },
  { id: 'term269', term: 'Despersonalização', definition: 'Sentimento de estar desligado de si mesmo, como se fosse um observador externo de seus próprios pensamentos ou corpo.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term270', term: 'Desrealização', definition: 'Sentimento de que o mundo ao redor não é real, como se estivesse em um sonho ou filme.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term271', term: 'Psicofarmacologia', definition: 'Estudo do uso de medicamentos para tratar transtornos mentais, modificando a química cerebral para aliviar sintomas.', category: 'Intervenção' },
  { id: 'term272', term: 'Contrato Comportamental', definition: 'Acordo escrito entre duas partes (ex: terapeuta e cliente, pais e filho) que detalha um comportamento alvo e as consequências (reforços) por realizá-lo.', category: 'Intervenção' },
  { id: 'term273', term: 'Cadeira de Rodas Motorizada', definition: 'Cadeira de rodas impulsionada por um motor elétrico, oferecendo independência de mobilidade para pessoas com limitações severas nos membros superiores.', category: 'Tecnologia e Suporte' },
  { id: 'term274', term: 'Adaptação Veicular', definition: 'Modificações feitas em um veículo para permitir que uma pessoa com deficiência possa dirigi-lo ou ser transportada com segurança e conforto.', category: 'Tecnologia e Suporte' },
  { id: 'term275', term: 'Privilégio da Habilidade Típica (Able-Bodied Privilege)', definition: 'Vantagens sociais, físicas e econômicas que pessoas sem deficiência possuem simplesmente por não terem suas capacidades questionadas ou limitadas por barreiras sociais.', category: 'Inclusão' },
  { id: 'term276', term: 'Narrativa Social', definition: 'História curta escrita em primeira pessoa que descreve uma situação social, habilidade ou conceito de forma clara e objetiva para ajudar a pessoa a entender e a se comportar em contextos específicos.', category: 'Intervenção' },
  { id: 'term277', term: 'Ageísmo', definition: 'Discriminação ou preconceito com base na idade, que pode se interseccionar com o capacitismo, afetando idosos com deficiência.', category: 'Inclusão' },
  { id: 'term278', term: 'Autocuidado', definition: 'Ações deliberadas que um indivíduo toma para cuidar de sua saúde física, mental e emocional. É essencial para prevenir o esgotamento, especialmente para cuidadores.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term279', term: 'Apoio de Pares', definition: 'Suporte oferecido por pessoas que compartilham experiências de vida semelhantes, como grupos de pais de crianças autistas ou grupos de autodefensoria.', category: 'Suporte e Reabilitação' },
  { id: 'term280', term: 'Habitação Inclusiva', definition: 'Moradias projetadas e localizadas para serem acessíveis e integradas na comunidade, permitindo que pessoas com deficiência vivam de forma independente.', category: 'Inclusão' },
  { id: 'term281', term: 'Serviços de Relevo (Respite Care)', definition: 'Cuidado temporário oferecido a uma pessoa com deficiência para que seus cuidadores familiares possam ter um período de descanso.', category: 'Suporte e Reabilitação' },
  { id: 'term282', term: 'Invisibilização', definition: 'Processo social que torna as pessoas com deficiência ou suas necessidades "invisíveis" para o resto da sociedade, seja por barreiras físicas ou atitudinais.', category: 'Inclusão' },
  { id: 'term283', term: 'Eugenismo', definition: 'Crença ou prática de melhorar a "qualidade" genética da população humana, historicamente usada para justificar atrocidades contra pessoas com deficiência.', category: 'Modelos Teóricos' },
  { id: 'term284', term: 'Trabalho com Significado', definition: 'Emprego que não apenas fornece sustento, mas também um senso de propósito, contribuição e identidade pessoal.', category: 'Inclusão' },
  { id: 'term285', term: 'Autoestima', definition: 'Avaliação subjetiva que uma pessoa faz de seu próprio valor. É construída a partir de experiências e do feedback de outros.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term286', term: 'Autoeficácia', definition: 'A crença na própria capacidade de organizar e executar as ações necessárias para atingir objetivos específicos.', category: 'Cognição' },
  { id: 'term287', term: 'Inteligência Fluida', definition: 'Capacidade de raciocinar, resolver problemas novos e identificar padrões, independentemente do conhecimento prévio.', category: 'Cognição' },
  { id: 'term288', term: 'Inteligência Cristalizada', definition: 'Conhecimento e habilidades acumuladas ao longo da vida, incluindo vocabulário e conhecimento geral.', category: 'Cognição' },
  { id: 'term289', term: 'Paternalismo', definition: 'Atitude de limitar a autonomia ou liberdade de uma pessoa ou grupo, sob o pretexto de que é para o seu próprio bem. Frequentemente direcionado a pessoas com deficiência.', category: 'Inclusão' },
  { id: 'term290', term: 'Empoderamento (Empowerment)', definition: 'Processo pelo qual indivíduos e grupos ganham poder e controle sobre suas próprias vidas e circunstâncias.', category: 'Direitos' },
  { id: 'term291', term: 'Design Afetivo', definition: 'Design que visa evocar emoções nos usuários para criar uma experiência positiva e memorável com um produto ou serviço.', category: 'Tecnologia e Suporte' },
  { id: 'term292', term: 'Mecanismos de Defesa', definition: 'Estratégias psicológicas inconscientes usadas para lidar com a ansiedade e proteger o ego de pensamentos ou sentimentos inaceitáveis.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term293', term: 'Consciência Situacional', definition: 'Percepção dos elementos ambientais e eventos em relação ao tempo e espaço, a compreensão de seu significado e a projeção de seu status futuro.', category: 'Cognição' },
  { id: 'term294', term: 'Fonoforense', definition: 'Aplicação dos conhecimentos da fonoaudiologia para fins legais, como na identificação de locutores ou na análise da veracidade de uma fala.', category: 'Intervenção' },
  { id: 'term295', term: 'Audiologia Educacional', definition: 'Área da audiologia focada em minimizar o impacto da deficiência auditiva no desenvolvimento e na educação da criança.', category: 'Educação Inclusiva' },
  { id: 'term296', term: 'Violência Capacitista', definition: 'Qualquer ato ou omissão que, baseado na deficiência, cause morte, dano ou sofrimento físico, sexual, psicológico ou patrimonial.', category: 'Direitos' },
  { id: 'term297', term: 'Teoria da Mente Corporalizada', definition: 'Extensão da Teoria da Mente que sugere que compreendemos os outros não apenas por inferência lógica, mas simulando suas ações e emoções em nosso próprio sistema motor e afetivo.', category: 'Modelos Teóricos' },
  { id: 'term298', term: 'Espelhamento (Mirroring)', definition: 'Ação de imitar inconscientemente a linguagem corporal, fala ou atitude de outra pessoa. Pessoas autistas podem ter dificuldade com isso ou fazê-lo de forma consciente como parte da máscara.', category: 'Comportamento' },
  { id: 'term299', term: 'Letramento Visual', definition: 'Habilidade de interpretar, negociar e criar significado a partir de informações apresentadas na forma de imagem.', category: 'Aprendizagem' },
  { id: 'term300', term: 'Cidadania Digital', definition: 'Normas de comportamento apropriado e responsável no uso da tecnologia. Envolve direitos e deveres, como o direito à acessibilidade digital e o dever de não cometer cyberbullying.', category: 'Inclusão' },
  { id: 'term301', term: 'Justiça da Deficiência (Disability Justice)', definition: 'Estrutura política criada por ativistas com deficiência, Pretas, Indígenas e Pessoas de Cor (BIPOC), queers e trans. Vai além dos direitos civis, focando em uma análise interseccional que aborda como raça, gênero, classe e outras identidades afetam o acesso e a experiência da deficiência.', category: 'Justiça e Ativismo' },
  { id: 'term302', term: 'Crip-Teoria (Crip Theory)', definition: 'Campo de estudo acadêmico e ativista que explora a deficiência a partir de uma perspectiva crítica, desafiando visões médicas e normalizadoras. Usa o termo "crip" (aleijado/defa) de forma reapropriada para questionar o que é considerado um corpo "normal" e funcional.', category: 'Teoria Crítica' },
  { id: 'term303', term: 'Capacitismo Racializado', definition: 'Forma como o capacitismo e o racismo se intersectam, em que corpos racializados são simultaneamente considerados "inferiores" e/ou "desviantes" tanto racialmente quanto em termos de capacidade, levando a diagnósticos errôneos, violência policial e negligência sistêmica.', category: 'Perspectivas Interseccionais' },
  { id: 'term304', term: 'Misoginia Capacitista (Ableist Misogyny)', definition: 'Intersecção do sexismo e do capacitismo, que invalida e oprime mulheres e pessoas identificadas como mulheres com deficiência, questionando sua capacidade de trabalho, maternidade, autonomia e sexualidade.', category: 'Perspectivas Interseccionais' },
  { id: 'term305', term: 'Tempo Crip (Crip Time)', definition: 'Conceito que descreve como pessoas com deficiência e doenças crônicas vivenciam o tempo de maneira diferente das expectativas capitalistas e normativas de produtividade. Valoriza a flexibilidade, a interdependência e os ritmos corporais únicos.', category: 'Teoria Crítica' },
  { id: 'term306', term: 'Justiça Reprodutiva e Deficiência', definition: 'Movimento que defende o direito de pessoas com deficiência de terem filhos, não terem filhos e criarem seus filhos em ambientes seguros e sustentáveis, lutando contra a esterilização forçada, a negação de cuidados e o estigma.', category: 'Justiça e Ativismo' },
  { id: 'term307', term: 'Epistemologias do Sul Global', definition: 'Valorização de saberes e formas de conhecimento produzidos por culturas e comunidades historicamente subjugadas pelo colonialismo. Na deficiência, busca entender e validar cosmologias e práticas de cuidado não-ocidentais.', category: 'Modelos Teóricos' },
  { id: 'term308', term: 'Necropolítica e Deficiência', definition: 'Aplicação do conceito de Achille Mbembe para descrever como o poder estatal e sistêmico determina quais vidas com deficiência são consideradas "dignas" de viver e quais são deixadas para morrer por meio da negligência, eugenia ou falta de recursos.', category: 'Teoria Crítica' },
  { id: 'term309', term: 'Biopoder', definition: 'Conceito de Foucault que descreve como o poder moderno se exerce sobre os corpos e a vida das populações, através de instituições médicas, psicológicas e estatais que classificam, normalizam e controlam quem é "saudável" ou "desviante".', category: 'Teoria Crítica' },
  { id: 'term310', term: 'Design Justice (Justiça no Design)', definition: 'Abordagem que busca desafiar a forma como o design distribui benefícios e desvantagens. Prioriza a liderança e as necessidades de comunidades marginalizadas no processo de criação, em vez de impor soluções.', category: 'Ética e Tecnologia' },
  { id: 'term311', term: 'Gordofobia e Capacitismo', definition: 'A sobreposição de preconceitos em que corpos gordos são patologizados e considerados indisciplinados, preguiçosos ou "não saudáveis", alinhando-se à lógica capacitista que valoriza apenas certos tipos de corpos e capacidades.', category: 'Perspectivas Interseccionais' },
  { id: 'term312', term: 'Cuidado Coletivo', definition: 'Prática política em que uma comunidade assume a responsabilidade mútua pelo bem-estar de seus membros, em oposição ao modelo individualista de autocuidado. É fundamental para a sobrevivência de ativistas e grupos marginalizados.', category: 'Justiça e Ativismo' },
  { id: 'term313', term: 'Extrativismo Epistêmico', definition: 'Prática acadêmica e institucional de extrair conhecimentos de comunidades marginalizadas (incluindo saberes sobre deficiência) sem o devido crédito, remuneração ou benefício para essas comunidades, perpetuando dinâmicas coloniais.', category: 'Teoria Crítica' },
  { id: 'term314', term: 'Acessibilidade Emocional', definition: 'Criação de espaços onde as pessoas se sintam seguras para expressar suas emoções de forma autêntica, sem serem julgadas, invalidadas ou punidas. Envolve práticas como validação, escuta ativa e tolerância à diversidade de expressões emocionais.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term315', term: 'Loucura como Resistência', definition: 'Perspectiva que enquadra certas manifestações da "loucura" ou sofrimento psíquico não como falhas individuais, mas como respostas legítimas a um mundo opressivo e doente.', category: 'Teoria Crítica' },
  { id: 'term316', term: 'Movimento Antimanicomial', definition: 'Luta social que defende o fim dos manicômios e uma transformação radical do cuidado em saúde mental, baseada na liberdade, na dignidade e na inserção comunitária, através de serviços como os CAPS (Centros de Atenção Psicossocial).', category: 'Justiça e Ativismo' },
  { id: 'term317', term: 'Psicologia da Libertação', definition: 'Abordagem da psicologia, originada na América Latina, que busca compreender e abordar o sofrimento a partir das condições de opressão social, política e econômica, visando a conscientização e a transformação social.', category: 'Modelos Teóricos' },
  { id: 'term318', term: 'Transcapacitismo (Transableism)', definition: 'Intersecção entre a transfobia e o capacitismo, que invalida as identidades de pessoas trans com deficiência, patologiza identidades de gênero diversas e cria barreiras no acesso à saúde e ao reconhecimento social.', category: 'Perspectivas Interseccionais' },
  { id: 'term319', term: 'Fúria Crip (Crip Rage)', definition: 'Raiva e indignação sentidas por pessoas com deficiência em resposta ao capacitismo sistêmico, à exclusão e à injustiça. É vista não como um sentimento negativo, mas como uma força potente para a mudança social.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term320', term: 'Colonialismo Médico', definition: 'Imposição de práticas, diagnósticos e entendimentos de saúde e doença ocidentais sobre populações não-ocidentais, desvalorizando e erradicando sistemas de cuidado e conhecimentos tradicionais e indígenas.', category: 'Teoria Crítica' },
  { id: 'term321', term: 'Trauma Intergeracional', definition: 'Trauma que é transmitido de uma geração para a outra através de mecanismos epigenéticos, comportamentais e sociais. É evidente em comunidades que sofreram opressão histórica, como o racismo e o colonialismo, impactando a saúde mental coletiva.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term322', term: 'Pedagogia da Acessibilidade', definition: 'Prática educacional que não apenas fornece adaptações, mas ensina ativamente a todos os alunos sobre deficiência, capacitismo e a importância da inclusão, tornando a própria acessibilidade um tema curricular.', category: 'Educação Inclusiva' },
  { id: 'term323', term: 'Linguagem Despatologizante', definition: 'Uso consciente da linguagem para descrever a diversidade humana sem recorrer a termos médicos ou de déficit, preferindo uma linguagem que afirma a identidade e a experiência (ex: "experiência autista" em vez de "sintomas do autismo").', category: 'Linguagem' },
  { id: 'term324', term: 'Intimidade Acessível', definition: 'Criação de condições para que pessoas com deficiência possam vivenciar a intimidade (emocional, física, sexual) de forma segura e consensual, o que envolve comunicação aberta sobre necessidades, acesso a apoios e combate a estigmas.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term325', term: 'Sobrevivência Coletiva', definition: 'Estratégias e práticas que grupos marginalizados criam para garantir sua sobrevivência e bem-estar diante da negligência do Estado e da violência sistêmica. Inclui redes de apoio mútuo, compartilhamento de recursos e conhecimento.', category: 'Justiça e Ativismo' },
  { id: 'term326', term: 'Etnocentrismo Diagnóstico', definition: 'A tendência de aplicar critérios diagnósticos baseados em normas culturais ocidentais, brancas e de classe média a todas as populações, o que pode levar a diagnósticos equivocados e à patologização da diversidade cultural.', category: 'Saúde e Classificação' },
  { id: 'term327', term: 'Sanismo (Sanism)', definition: 'Forma de discriminação e opressão contra pessoas que têm ou são rotuladas como tendo transtornos mentais. É análogo ao capacitismo, mas focado especificamente na saúde mental.', category: 'Inclusão' },
  { id: 'term328', term: 'Mad Pride (Orgulho Louco)', definition: 'Movimento de usuários e sobreviventes de serviços de saúde mental que reivindica a identidade "louca" como uma posição de poder e resistência, celebrando a neurodiversidade e lutando contra o estigma.', category: 'Justiça e Ativismo' },
  { id: 'term329', term: 'Acessibilidade Linguística', definition: 'Garantir que a comunicação seja compreensível para pessoas com diferentes níveis de letramento, proficiência no idioma local, ou deficiências cognitivas. Inclui o uso de Linguagem Simples, tradução e interpretação.', category: 'Comunicação' },
  { id: 'term330', term: 'Justiça Climática e Deficiência', definition: 'Análise de como as mudanças climáticas impactam desproporcionalmente as pessoas com deficiência, que são mais vulneráveis a desastres naturais, temperaturas extremas e interrupções em serviços de saúde, e a luta por sua inclusão em políticas climáticas.', category: 'Justiça e Ativismo' },
  { id: 'term331', term: 'Crip-de-alegria (Crip-joy)', definition: 'A alegria e o prazer que pessoas com deficiência encontram em seus corpos, suas comunidades e suas formas únicas de ser e estar no mundo, como um ato de resistência contra uma cultura que associa deficiência ao sofrimento.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term332', term: 'Reapropriação de Termos', definition: 'Ato político de um grupo marginalizado tomar para si um termo historicamente usado para ofendê-lo (como "crip", "queer" ou "louco") e ressignificá-lo como uma fonte de identidade e orgulho.', category: 'Linguagem' },
  { id: 'term333', term: 'Ouvintismo', definition: 'Sistema de opressão e privilégio que coloca a experiência de ouvir e a comunicação oral como o padrão superior e "normal", marginalizando pessoas surdas, suas línguas de sinais e culturas.', category: 'Inclusão' },
  { id: 'term334', term: 'Justiça Restaurativa', definition: 'Abordagem para lidar com o dano e o conflito que foca na reparação das relações e na responsabilização, em vez de punição. Pode ser usada para mediar conflitos relacionados a capacitismo e exclusão.', category: 'Justiça e Ativismo' },
  { id: 'term335', term: 'Abandono Benevolente', definition: 'Situação em que pessoas com deficiência são deixadas de lado ou excluídas de oportunidades (sociais, educacionais, profissionais) não por malícia explícita, mas por uma crença capacitista de que estão "melhor" ou "mais seguras" em ambientes segregados ou sem desafios.', category: 'Inclusão' },
  { id: 'term336', term: 'Contranarrativas', definition: 'Narrativas criadas por grupos marginalizados para desafiar e corrigir as histórias dominantes que os estereotipam, silenciam ou desumanizam.', category: 'Linguagem' },
  { id: 'term337', term: 'Humildade Cultural', definition: 'Postura de aprendizado contínuo e auto-reflexão sobre o próprio lugar de poder e privilégio ao interagir com pessoas de culturas diferentes. É o oposto de "competência cultural", que sugere que se pode "dominar" outra cultura.', category: 'Intervenção' },
  { id: 'term338', term: 'Economia do Cuidado (Care Economy)', definition: 'Setor da economia que envolve o trabalho de cuidado, tanto remunerado quanto não-remunerado (majoritariamente realizado por mulheres, especialmente mulheres racializadas). A justiça da deficiência defende sua valorização e justa remuneração.', category: 'Teoria Crítica' },
  { id: 'term339', term: 'Oralismo', definition: 'Método educacional que força pessoas surdas a aprenderem a falar e a ler os lábios, proibindo ou desencorajando o uso da língua de sinais. É considerado uma prática colonialista e opressiva pela comunidade surda.', category: 'Educação Inclusiva' },
  { id: 'term340', term: 'Leitura de Sensibilidade (Sensitivity Reading)', definition: 'Processo em que um autor contrata uma pessoa pertencente a um grupo marginalizado para ler um manuscrito e identificar representações problemáticas, estereótipos ou imprecisões sobre aquele grupo.', category: 'Linguagem' },
  { id: 'term341', term: 'Solidariedade Interseccional', definition: 'A prática de construir alianças entre diferentes movimentos de justiça social (antirracista, feminista, LGBTQIA+, deficiência), reconhecendo que as opressões estão interligadas e que a libertação de um grupo depende da libertação de todos.', category: 'Justiça e Ativismo' },
  { id: 'term342', term: 'Segurança Psicológica', definition: 'Crença compartilhada pelos membros de uma equipe de que o ambiente é seguro para a tomada de riscos interpessoais. Em contextos inclusivos, significa que se pode revelar uma deficiência ou necessidade sem medo de retaliação.', category: 'Inclusão' },
  { id: 'term343', term: 'Audismo Institucional', definition: 'Formas como instituições (escolas, hospitais, sistema de justiça) sistematicamente privilegiam pessoas ouvintes e criam barreiras para pessoas surdas através de políticas, práticas e falta de acessibilidade em Libras.', category: 'Inclusão' },
  { id: 'term344', term: 'Medicalização da Vida', definition: 'Processo pelo qual experiências humanas normais (tristeza, envelhecimento, timidez) são redefinidas como problemas médicos, necessitando de diagnóstico e tratamento. O capacitismo expande essa medicalização para qualquer variação corporal ou neurológica.', category: 'Teoria Crítica' },
  { id: 'term345', term: 'Modelo de Recuperação em Saúde Mental', definition: 'Abordagem que enfatiza a esperança, o empoderamento e a autonomia, focando na capacidade do indivíduo de construir uma vida significativa, com ou sem a presença contínua de sintomas, em oposição a um modelo focado apenas na "cura".', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term346', term: 'Alfabetização Crítica', definition: 'Abordagem do letramento que incentiva os leitores a questionar ativamente os textos, analisar as relações de poder, identificar vieses e entender como as narrativas constroem a realidade social.', category: 'Aprendizagem' },
  { id: 'term347', term: 'Patologização da Pobreza', definition: 'Tendência de atribuir a pobreza a falhas morais, psicológicas ou comportamentais dos indivíduos (ex: "preguiça", "falta de inteligência"), em vez de reconhecer as causas estruturais como o racismo, o capacitismo e a desigualdade econômica.', category: 'Teoria Crítica' },
  { id: 'term348', term: 'Cissexismo Capacitista', definition: 'A intersecção do cissexismo (privilegiar identidades cisgênero) com o capacitismo, por exemplo, na forma como os corpos trans e não-binários são patologizados ou como o acesso a cuidados de afirmação de gênero é negado com base na deficiência.', category: 'Perspectivas Interseccionais' },
  { id: 'term349', term: 'Desinvestimento Institucional', definition: 'A prática de instituições (governos, empresas) de retirar recursos e apoio de serviços públicos essenciais (saúde, educação, moradia), o que impacta desproporcionalmente pessoas com deficiência e outras comunidades marginalizadas.', category: 'Justiça e Ativismo' },
  { id: 'term350', term: 'Estudos Surdos (Deaf Studies)', definition: 'Campo acadêmico multidisciplinar que estuda a experiência surda não como uma deficiência médica, mas como uma identidade cultural e linguística, focando na língua de sinais, na história surda e na arte e literatura surdas.', category: 'Modelos Teóricos' },
  // Os próximos 150 termos continuarão a aprofundar esses temas.
  { id: 'term351', term: 'Corponormatividade', definition: 'A expectativa social implícita de que todos os corpos devem ser de uma certa forma: sem deficiência, cisgênero, magro, jovem e funcional de acordo com padrões produtivistas.', category: 'Teoria Crítica' },
  { id: 'term352', term: 'Ativismo de Sofá (Slacktivism)', definition: 'Crítica a ações de ativismo digital que exigem pouco esforço (como curtidas ou compartilhamentos) e que podem gerar uma sensação de engajamento sem levar a mudanças concretas. O debate envolve se é um ponto de entrada válido ou uma distração.', category: 'Justiça e Ativismo' },
  { id: 'term353', term: 'Capitalismo de Vigilância', definition: 'Modelo econômico que se baseia na coleta e mercantilização de dados pessoais. Para pessoas com deficiência, isso pode significar vigilância sobre o uso de benefícios, monitoramento de comportamentos e venda de dados de saúde.', category: 'Ética e Tecnologia' },
  { id: 'term354', term: 'Abolicionismo Penal e Deficiência', definition: 'Movimento que busca o fim das prisões e da polícia, argumentando que são sistemas inerentemente violentos e capacitistas, que criminalizam desproporcionalmente pessoas com deficiência e transtornos mentais, especialmente se forem racializadas.', category: 'Justiça e Ativismo' },
  { id: 'term355', term: 'Estética do Acesso', definition: 'Prática de integrar a acessibilidade ao processo criativo desde o início, tratando-a não como uma obrigação técnica, mas como uma oportunidade estética e expressiva. Ex: legendas que fazem parte do design do vídeo.', category: 'Inclusão' },
  { id: 'term356', term: 'Teatro do Oprimido', definition: 'Método teatral criado por Augusto Boal que utiliza o teatro como ferramenta de conscientização e transformação social, permitindo que os participantes (espect-atores) ensaiem ações para superar opressões da vida real.', category: 'Intervenção' },
  { id: 'term357', term: 'Epistemologia da Ignorância', definition: 'Estudo de como a ignorância sobre as experiências de grupos marginalizados é ativamente produzida e mantida pela sociedade para sustentar estruturas de poder. Ex: a ignorância da sociedade ouvinte sobre a riqueza da Libras.', category: 'Teoria Crítica' },
  { id: 'term358', term: 'Letramento Emocional', definition: 'Habilidade de reconhecer, compreender, rotular e expressar as próprias emoções e as dos outros. É uma base fundamental para a regulação emocional e a empatia.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term359', term: 'Curadoria Crip', definition: 'A prática de curadores com deficiência selecionarem e exibirem arte e cultura de uma perspectiva crip, desafiando a exclusão de artistas com deficiência dos espaços de arte convencionais e contando suas próprias histórias.', category: 'Inclusão' },
  { id: 'term360', term: 'Colonialidade do Poder', definition: 'Conceito de Aníbal Quijano que afirma que, mesmo após o fim do colonialismo formal, a lógica de dominação colonial (racial, epistêmica, econômica) persiste nas estruturas sociais e institucionais do presente.', category: 'Modelos Teóricos' },
  { id: 'term361', term: 'Fadiga da Decisão', definition: 'O esgotamento da capacidade de tomar decisões após um longo período de escolhas. Para pessoas com deficiência, isso é exacerbado pela necessidade constante de planejar acessibilidade, administrar cuidados e lutar por direitos.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term362', term: 'Felicidade Compulsória', definition: 'A pressão social para parecer feliz e otimista o tempo todo, o que pode ser particularmente opressivo para pessoas com dor crônica ou sofrimento psíquico, invalidando suas experiências.', category: 'Inclusão' },
  { id: 'term363', term: 'Solastalgia', definition: 'A angústia causada pela mudança ambiental negativa em um local que se ama. Pessoas com deficiência podem sentir isso de forma aguda, pois sua conexão e acesso a ambientes naturais podem ser mais frágeis.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term364', term: 'Práticas Restaurativas', definition: 'Processos proativos que buscam construir comunidade e relacionamentos para prevenir conflitos. Em escolas, podem criar um clima mais inclusivo e reduzir o bullying capacitista.', category: 'Educação Inclusiva' },
  { id: 'term365', term: 'Hospitalidade Radical', definition: 'Prática de criar espaços que não apenas "toleram" a diferença, mas que a acolhem ativamente e se transformam para atender às necessidades de todos, especialmente dos mais marginalizados.', category: 'Inclusão' },
  { id: 'term366', term: 'Feminismo da Deficiência (Disability Feminism)', definition: 'Vertente do feminismo que analisa como o patriarcado e o capacitismo estão entrelaçados, criticando a idealização de corpos e mentes "perfeitos" e lutando por uma visão de libertação que seja inclusiva para todas as mulheres.', category: 'Perspectivas Interseccionais' },
  { id: 'term367', term: 'Indústria da Inspiração (Inspiration Porn)', definition: 'Termo cunhado por Stella Young para descrever a objetificação de pessoas com deficiência em narrativas que as apresentam como "inspiradoras" por simplesmente viverem suas vidas. Serve para fazer pessoas sem deficiência se sentirem bem, em vez de abordar as barreiras sistêmicas.', category: 'Linguagem' },
  { id: 'term368', term: 'Consentimento Entusiasmado', definition: 'Padrão de consentimento que defende que a permissão para qualquer interação (especialmente sexual) deve ser um "sim" claro, coerente e entusiasmado, e não a ausência de um "não". É crucial em discussões sobre sexualidade e deficiência.', category: 'Direitos' },
  { id: 'term369', term: 'Alfabetização Midiática Crítica', definition: 'Habilidade de acessar, analisar, avaliar e criar mídia de forma crítica, reconhecendo como as representações da mídia moldam percepções sobre deficiência, raça, gênero e outras identidades.', category: 'Aprendizagem' },
  { id: 'term370', term: 'Infantilização', definition: 'Tratar uma pessoa adulta (especialmente uma pessoa com deficiência intelectual ou física) como se fosse uma criança, negando sua autonomia, capacidade de decisão e sexualidade.', category: 'Inclusão' },
  { id: 'term371', term: 'Gentrificação', definition: 'Processo de "enobrecimento" de um bairro que expulsa moradores de baixa renda. Isso afeta desproporcionalmente pessoas com deficiência, que perdem acesso a moradias acessíveis, redes de cuidado e serviços locais.', category: 'Justiça e Ativismo' },
  { id: 'term372', term: 'Interrupção (Disruption)', definition: 'Ato deliberado de quebrar o fluxo "normal" das coisas para expor e desafiar uma injustiça. Protestos e performances de ativistas com deficiência podem ser vistos como atos de interrupção criativa.', category: 'Justiça e Ativismo' },
  { id: 'term373', term: 'Soberania Alimentar', definition: 'O direito dos povos de definirem suas próprias políticas e sistemas alimentares. Para pessoas com deficiência, isso se conecta ao acesso a alimentos nutritivos e culturalmente apropriados, e à luta contra a seletividade forçada pela pobreza.', category: 'Direitos' },
  { id: 'term374', term: 'Ecologia da Deficiência', definition: 'Estudo de como os ambientes físicos, sociais e culturais moldam a experiência da deficiência. Enfatiza que a mudança no ecossistema (ex: criando mais acessibilidade) pode "desabilitar" barreiras.', category: 'Modelos Teóricos' },
  { id: 'term375', term: 'Terapia Afirmativa', definition: 'Abordagem terapêutica que valida e afirma as identidades de clientes pertencentes a grupos minoritários (ex: LGBTQIA+, neurodivergentes), em vez de tentar "corrigi-las" ou tratá-las como a fonte do problema.', category: 'Intervenção' },
  { id: 'term376', term: 'Desobediência Civil', definition: 'Ato de se recusar a obedecer a certas leis consideradas injustas, como forma de protesto pacífico. Historicamente, foi usada por movimentos de direitos civis, incluindo o da deficiência (ex: protestos por acessibilidade no transporte).', category: 'Justiça e Ativismo' },
  { id: 'term377', term: 'Capacitismo Benevolente', definition: 'Atitudes e comportamentos que parecem positivos ou de ajuda, mas que se baseiam na crença de que pessoas com deficiência são inerentemente incapazes e precisam de proteção, minando sua autonomia.', category: 'Inclusão' },
  { id: 'term378', term: 'Cultura da Produtividade Tóxica', definition: 'Mentalidade social que equipara o valor humano à produtividade, levando ao esgotamento (burnout) e estigmatizando quem não pode ou não quer seguir esse ritmo, como muitas pessoas com deficiência.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term379', term: 'Paradigma da Neurodiversidade', definition: 'Mudança de perspectiva que propõe que variações neurológicas como autismo, TDAH e dislexia não são "transtornos" a serem curados, mas parte natural e valiosa da diversidade humana.', category: 'Modelos Teóricos' },
  { id: 'term380', term: 'Morte Social', definition: 'Condição em que uma pessoa é tratada como se estivesse morta ou não existisse, sendo excluída da vida social, despojada de seus direitos e de sua identidade. Pode ser imposta a pessoas em instituições ou com deficiências severas.', category: 'Teoria Crítica' },
  { id: 'term381', term: 'Privilégio Temporal', definition: 'Vantagem não-ganha de ter um corpo e uma mente que se alinham com as expectativas sociais de tempo, velocidade e produtividade, permitindo navegar facilmente por sistemas que não acomodam o "Tempo Crip".', category: 'Teoria Crítica' },
  { id: 'term382', term: 'Justiça Linguística', definition: 'Garantir que todos possam se comunicar e ser compreendidos no idioma em que se sentem mais confortáveis e proficientes, incluindo línguas de sinais, línguas indígenas e outras variações linguísticas.', category: 'Direitos' },
  { id: 'term383', term: 'Super-representação em Medida Socioeducativa', definition: 'A sobrerrepresentação de jovens com deficiência (especialmente intelectual e de aprendizagem) e racializados no sistema de justiça juvenil, indicando falhas do sistema educacional e diagnóstico usado como ferramenta de criminalização.', category: 'Perspectivas Interseccionais' },
  { id: 'term384', term: 'Viés de Normalidade', definition: 'Tendência cognitiva de subestimar a possibilidade de um desastre ou crise e seu impacto. Dificulta a criação de planos de emergência inclusivos que considerem as necessidades de pessoas com deficiência.', category: 'Cognição' },
  { id: 'term385', term: 'Capitalismo Racial', definition: 'Teoria de Cedric Robinson que argumenta que o capitalismo não é apenas cúmplice, mas dependente do racismo para criar hierarquias, explorar mão de obra e acumular riqueza, o que se conecta ao modo como corpos racializados e com deficiência são explorados.', category: 'Teoria Crítica' },
  { id: 'term386', term: 'Fragilidade Branca', definition: 'Estado de desconforto e defensividade de uma pessoa branca quando confrontada com informações sobre injustiça e privilégio racial. Pode se manifestar como raiva, medo ou culpa, interrompendo o diálogo necessário sobre racismo.', category: 'Inclusão' },
  { id: 'term387', term: 'Comunidades Intencionais', definition: 'Comunidades criadas por pessoas que escolhem viver juntas com base em valores e objetivos compartilhados. Podem ser uma forma de criar ambientes de vida mais acessíveis e baseados no cuidado coletivo.', category: 'Inclusão' },
  { id: 'term388', term: 'Amor Radical', definition: 'Em contextos de ativismo, refere-se a um amor que não é apenas um sentimento, mas uma prática política de compaixão, solidariedade e compromisso com a libertação de todos, mesmo daqueles com quem discordamos.', category: 'Justiça e Ativismo' },
  { id: 'term389', term: 'Sustentabilidade do Ativismo', definition: 'Práticas e estratégias para garantir que ativistas possam continuar seu trabalho a longo prazo sem sofrer esgotamento (burnout), o que envolve cuidado coletivo, limites e distribuição de tarefas.', category: 'Justiça e Ativismo' },
  { id: 'term390', term: 'Arte Bruta (Art Brut) / Arte Outsider', definition: 'Arte criada por pessoas fora do sistema de arte convencional, como autodidatas, pessoas em instituições psiquiátricas ou com deficiências. O termo é debatido por seu potencial de ser tanto celebratório quanto estigmatizante.', category: 'Inclusão' },
  { id: 'term391', term: 'Descolonização do Currículo', definition: 'Processo de repensar e reestruturar o currículo educacional para incluir perspectivas, autores e conhecimentos de fora do cânone ocidental e branco, desafiando narrativas coloniais.', category: 'Educação Inclusiva' },
  { id: 'term392', term: 'Tecno-solucionismo', definition: 'A crença equivocada de que problemas sociais complexos, como o capacitismo, podem ser resolvidos simplesmente com mais e melhores tecnologias, ignorando as questões políticas, culturais e econômicas subjacentes.', category: 'Ética e Tecnologia' },
  { id: 'term393', term: 'Capacitismo Hostil', definition: 'Forma explícita e agressiva de capacitismo, que envolve insultos, violência, exclusão deliberada e a crença de que pessoas com deficiência são inferiores.', category: 'Inclusão' },
  { id: 'term394', term: 'Pensamento Sistêmico', definition: 'Abordagem para a resolução de problemas que foca em ver as partes de um sistema em relação ao todo. Ajuda a entender como o capacitismo não é apenas um preconceito individual, mas está embutido em políticas, instituições e cultura.', category: 'Modelos Teóricos' },
  { id: 'term395', term: 'Autonomia Relacional', definition: 'Conceito feminista que desafia a ideia de autonomia como independência total. Propõe que a autonomia é exercida dentro de um contexto de relações de interdependência e cuidado, o que é mais alinhado à experiência da deficiência.', category: 'Teoria Crítica' },
  { id: 'term396', term: 'Dignidade do Risco', definition: 'O direito de toda pessoa, incluindo aquelas com deficiência, de fazer suas próprias escolhas e correr riscos, mesmo que isso possa levar a erros ou fracassos. É fundamental para o aprendizado, o crescimento e a autodeterminação.', category: 'Direitos' },
  { id: 'term397', term: 'Luto Ecológico', definition: 'O luto sentido em resposta a perdas ecológicas, como a extinção de espécies ou a destruição de ecossistemas. Pode ser particularmente agudo para povos indígenas e comunidades cuja cultura está ligada à terra.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term398', term: 'Imaginação Radical', definition: 'A capacidade de imaginar e sonhar com mundos, sistemas e formas de relacionamento que estão para além das opressões do presente. É uma ferramenta essencial para o ativismo, pois permite visualizar o objetivo da luta.', category: 'Justiça e Ativismo' },
  { id: 'term399', term: 'Co-regulação', definition: 'Processo em que uma pessoa ajuda outra a regular seu estado emocional e fisiológico através de sua presença calma e responsiva. É a base da autorregulação, aprendida inicialmente na relação cuidador-criança.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term400', term: 'Esperança Crítica', definition: 'Uma esperança que não é otimismo cego, mas sim uma prática consciente que reconhece as dificuldades e injustiças do presente, mas se engaja na luta por um futuro melhor, baseada na solidariedade e na ação.', category: 'Justiça e Ativismo' },
  // 401-500: Aprofundamento e nichos
  { id: 'term401', term: 'Políticas de Identidade', definition: 'Estratégia política onde grupos com identidades marginalizadas (raça, gênero, deficiência) se unem para lutar por seus direitos específicos. É debatida por seu poder de mobilização versus o risco de fragmentação.', category: 'Justiça e Ativismo' },
  { id: 'term402', term: 'Xenofobia Capacitista', definition: 'A intersecção da xenofobia com o capacitismo, onde imigrantes e refugiados são vistos como um "fardo" para o sistema de saúde ou social, e suas deficiências são usadas como justificativa para negar entrada ou direitos.', category: 'Perspectivas Interseccionais' },
  { id: 'term403', term: 'Conhecimento Situado', definition: 'Conceito de Donna Haraway que argumenta que todo conhecimento é parcial e produzido a partir de uma perspectiva corporal e social específica. Desafia a noção de objetividade universal e valoriza o conhecimento vindo de experiências marginalizadas.', category: 'Modelos Teóricos' },
  { id: 'term404', term: 'Capitalismo Cognitivo', definition: 'Fase do capitalismo onde o principal recurso para a acumulação de valor é o conhecimento, a informação e a criatividade. Isso cria novas formas de exclusão para aqueles cujo processamento cognitivo diverge da norma.', category: 'Teoria Crítica' },
  { id: 'term405', term: 'Direito à Cidade', definition: 'O direito de todos os habitantes, especialmente os marginalizados, de acessar, usar e moldar a cidade de acordo com suas necessidades. Inclui o direito à moradia, transporte acessível e espaços públicos inclusivos.', category: 'Direitos' },
  { id: 'term406', term: 'Gaslighting Médico', definition: 'Situação em que profissionais de saúde minimizam, invalidam ou atribuem a causas psicológicas os sintomas físicos de um paciente, fazendo-o duvidar de sua própria percepção da realidade. É comum com mulheres e pessoas com doenças crônicas e "invisíveis".', category: 'Violência e Opressão' },
  { id: 'term407', term: 'Vulnerabilidade Estratégica', definition: 'Ato consciente de se mostrar vulnerável em um espaço seguro para construir confiança, aprofundar relacionamentos e promover a cura coletiva. É uma ferramenta poderosa em grupos de apoio e ativismo.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term408', term: 'Cultura do Cancelamento', definition: 'Prática de retirar o apoio público (boicotar, "cancelar") a figuras públicas ou empresas após expressarem opiniões ou comportamentos considerados ofensivos. Seu papel na responsabilização versus o risco de punitivismo é amplamente debatido.', category: 'Justiça e Ativismo' },
  { id: 'term409', term: 'Decolonialidade do Ser', definition: 'O processo de desaprender as formas de ser, pensar e sentir impostas pelo colonialismo, que nos alienam de nossos corpos, nossas comunidades e nossas histórias, em busca de uma existência mais autêntica e libertadora.', category: 'Teoria Crítica' },
  { id: 'term410', term: 'Bioética Crítica da Deficiência', definition: 'Campo que examina questões éticas relacionadas à vida, saúde e tecnologia (como edição genética, aborto seletivo) a partir da perspectiva dos direitos e da experiência das pessoas com deficiência, desafiando a lógica eugenista.', category: 'Ética e Tecnologia' },
  { id: 'term411', term: 'Sobrecarga de Empatia', definition: 'Estado de exaustão emocional e física causado pela exposição contínua ao sofrimento de outros. Afeta muitos cuidadores e profissionais de ajuda, destacando a necessidade de cuidado coletivo e limites.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term412', term: 'Arquitetura Hostil', definition: 'Design urbano que deliberadamente desencoraja o uso de espaços públicos por certas pessoas, como pinos em bancos para evitar que pessoas em situação de rua durmam. É uma forma de capacitismo e classismo inscrita no ambiente.', category: 'Violência e Opressão' },
  { id: 'term413', term: 'Feminismo Antirracista', definition: 'Vertente do feminismo que argumenta que a luta pela libertação das mulheres não pode ser separada da luta contra o racismo, reconhecendo que mulheres racializadas enfrentam formas únicas e sobrepostas de opressão.', category: 'Perspectivas Interseccionais' },
  { id: 'term414', term: 'Direito ao Ócio', definition: 'O direito de todas as pessoas ao descanso, ao lazer e a atividades não-produtivas, como uma forma de resistência à cultura da produtividade tóxica e como um componente essencial do bem-estar.', category: 'Direitos' },
  { id: 'term415', term: 'Justiça Transformativa', definition: 'Abordagem para lidar com a violência que busca não apenas reparar o dano (como na justiça restaurativa), mas também transformar as condições sistêmicas (pobreza, racismo, capacitismo) que permitiram que a violência acontecesse.', category: 'Justiça e Ativismo' },
  { id: 'term416', term: 'Performance de Gênero', definition: 'Conceito de Judith Butler que afirma que o gênero não é uma essência interna, mas uma identidade construída através de atos, gestos e discursos repetidos ao longo do tempo. Ajuda a entender a fluidez e a construção social do gênero.', category: 'Teoria Crítica' },
  { id: 'term417', term: 'Tecno-otimismo Cego', definition: 'A crença acrítica de que a tecnologia é inerentemente neutra e sempre levará ao progresso social, ignorando como ela pode replicar e amplificar vieses capacitistas, racistas e sexistas.', category: 'Ética e Tecnologia' },
  { id: 'term418', term: 'Mapeamento Comunitário', definition: 'Processo em que os próprios membros de uma comunidade coletam dados e criam mapas sobre seus recursos, necessidades e histórias, como uma forma de produzir conhecimento local e subsidiar a luta por direitos.', category: 'Justiça e Ativismo' },
  { id: 'term419', term: 'Pedagogias Crip', definition: 'Práticas de ensino que centram a experiência da deficiência, usando conceitos como "Tempo Crip" e interdependência para criar ambientes de aprendizagem mais flexíveis, colaborativos e acessíveis para todos.', category: 'Educação Inclusiva' },
  { id: 'term420', term: 'Trauma de Traição', definition: 'Tipo de trauma que ocorre quando uma pessoa ou instituição de quem dependemos para sobreviver (como pais ou o governo) viola nossa confiança e nos causa dano. É central para entender o impacto da negligência estatal sobre pessoas com deficiência.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term421', term: 'Parentalidade Neurodivergente', definition: 'A experiência de pais e cuidadores que são eles mesmos neurodivergentes (autistas, TDAH, etc.), com seus desafios únicos (sobrecarga, funções executivas) e pontos fortes (empatia, aceitação da diferença).', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term422', term: 'Direito à Preguiça', definition: 'Argumento político que desafia a ética de trabalho capitalista, afirmando o direito de se recusar a participar de sistemas de exploração e de valorizar a vida para além da produtividade.', category: 'Teoria Crítica' },
  { id: 'term423', term: 'Classismo', definition: 'Discriminação e preconceito com base na classe social. Intersecta-se com o capacitismo, pois a pobreza limita o acesso a diagnósticos, tratamentos e tecnologia, enquanto a deficiência pode levar ao empobrecimento.', category: 'Perspectivas Interseccionais' },
  { id: 'term424', term: 'Espiritualidade e Deficiência', definition: 'Exploração das diversas formas como pessoas com deficiência vivenciam a espiritualidade, a fé e a religião, tanto como fonte de conforto e comunidade, quanto como espaço de exclusão e capacitismo teológico.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term425', term: 'Tradução Cultural', definition: 'O trabalho, muitas vezes invisível, de mediar e explicar as normas e expectativas de uma cultura para outra. Pessoas neurodivergentes frequentemente realizam essa tradução entre a cultura neurotípica e a sua.', category: 'Comunicação' },
  // ... e assim por diante, completando os 500 termos com essa profundidade.
  // Gerando os 75 termos restantes para completar os 500
  { id: 'term426', term: 'Alfabetização em Saúde Mental', definition: 'Conhecimento e crenças sobre transtornos mentais que auxiliam em seu reconhecimento, manejo ou prevenção. A perspectiva crítica questiona quais conhecimentos são validados e como evitar a patologização.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term427', term: 'Economia Solidária', definition: 'Modelo de produção, consumo e distribuição de riqueza centrado na valorização do ser humano e na cooperação, não na competição e no lucro. Pode criar oportunidades de trabalho inclusivas para pessoas com deficiência.', category: 'Inclusão' },
  { id: 'term428', term: 'Comunidades de Prática', definition: 'Grupos de pessoas que se reúnem para aprofundar seu conhecimento e paixão por um tópico específico, aprendendo uns com os outros. Podem ser espaços vitais de apoio e troca para pessoas com deficiências raras.', category: 'Suporte e Reabilitação' },
  { id: 'term429', term: 'Biotipologia Colonial', definition: 'A prática pseudocientífica do colonialismo de classificar e hierarquizar corpos humanos com base em características físicas para justificar a dominação racial e a exploração.', category: 'Teoria Crítica' },
  { id: 'term430', term: 'Trabalho de Luto', definition: 'O processo psicológico de lidar com uma perda significativa. Em uma perspectiva decolonial, inclui o luto por perdas culturais, linguísticas e territoriais impostas pelo colonialismo.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term431', term: 'Design Especulativo', definition: 'Prática de design que cria protótipos e cenários de futuros possíveis para provocar o debate sobre as implicações sociais e éticas das novas tecnologias. Pode ser usado para imaginar futuros mais acessíveis e justos.', category: 'Ética e Tecnologia' },
  { id: 'term432', term: 'Apropriacionismo Cultural', definition: 'Adoção de elementos de uma cultura minoritária por membros da cultura dominante, de forma descontextualizada ou desrespeitosa, muitas vezes para fins comerciais. Pode ocorrer com símbolos ou práticas de comunidades de pessoas com deficiência.', category: 'Inclusão' },
  { id: 'term433', term: 'Justiça de Dados', definition: 'Luta para garantir que a forma como os dados são coletados, analisados e usados seja justa, equitativa e não reforce as desigualdades existentes. Foca em direitos de dados, governança e representação.', category: 'Ética e Tecnologia' },
  { id: 'term434', term: 'Psicologia Comunitária', definition: 'Abordagem da psicologia que enfatiza o contexto social e ambiental, em vez de focar apenas no indivíduo. Promove o bem-estar através da intervenção em nível comunitário e da mudança social.', category: 'Modelos Teóricos' },
  { id: 'term435', term: 'Síndrome do Impostor', definition: 'Sentimento persistente de inadequação e dúvida sobre as próprias conquistas, apesar de evidências externas de competência. É exacerbado em pessoas de grupos marginalizados, cujas habilidades são constantemente questionadas.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term436', term: 'Pluriverso', definition: 'Conceito decolonial que propõe a existência de múltiplos mundos e formas de conhecimento, em oposição à ideia de um "universo" único e universal dominado pela lógica ocidental. Valoriza a diversidade de ser e saber.', category: 'Modelos Teóricos' },
  { id: 'term437', term: 'Exaustão por Decisão', definition: 'Forma de esgotamento mental que ocorre pela necessidade contínua de tomar decisões, especialmente quando cada escolha tem altas apostas. Cuidadores e pessoas com deficiência frequentemente enfrentam isso no gerenciamento da saúde e da vida diária.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term438', term: 'Estudos Críticos dos Animais', definition: 'Campo que examina as relações humano-animais a partir de uma perspectiva ética e política, questionando o especismo. Tem interseções com os estudos da deficiência ao analisar como a "normalidade" e a "capacidade" são definidas para diferentes espécies.', category: 'Teoria Crítica' },
  { id: 'term439', term: 'Colorismo', definition: 'Forma de discriminação racial em que pessoas com tons de pele mais claros são privilegiadas em detrimento daquelas com tons de pele mais escuros, mesmo dentro da mesma comunidade racial.', category: 'Perspectivas Interseccionais' },
  { id: 'term440', term: 'Educação para a Paz', definition: 'Processo de adquirir valores, conhecimentos e desenvolver atitudes, habilidades e comportamentos para viver em harmonia consigo mesmo, com os outros e com o meio ambiente. Inclui o combate à violência capacitista.', category: 'Educação Inclusiva' },
  { id: 'term441', term: 'Fadiga da Compaixão', definition: 'Forma de estresse resultante da exposição ao trauma de outras pessoas (trauma vicário). Diferente do burnout, está mais ligada à exaustão da capacidade de empatizar. Relevante para profissionais de saúde e cuidadores.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term442', term: 'Cultura do Consentimento', definition: 'Cultura em que o consentimento é a norma, sendo livremente dado, entusiástico, específico e reversível. Luta para substituir uma cultura de coerção e violência.', category: 'Direitos' },
  { id: 'term443', term: 'Tokenismo', definition: 'A prática de fazer apenas um esforço superficial ou simbólico para incluir membros de grupos minoritários, a fim de dar a aparência de diversidade e evitar críticas de discriminação. Ex: contratar uma única pessoa com deficiência para "cumprir cota".', category: 'Inclusão' },
  { id: 'term444', term: 'Abolicionismo da Família', definition: 'Posição política que critica a família nuclear como uma instituição que privatiza o cuidado, reforça hierarquias de gênero e isola as pessoas, defendendo formas mais coletivas e comunitárias de cuidado e parentesco.', category: 'Teoria Crítica' },
  { id: 'term445', term: 'Segurança Hídrica', definition: 'Acesso confiável a uma quantidade e qualidade adequadas de água para saúde, subsistência e produção. A falta de segurança hídrica afeta desproporcionalmente comunidades com deficiência.', category: 'Direitos' },
  { id: 'term446', term: 'Prefiguração', definition: 'Prática ativista de criar e experimentar as relações sociais e instituições do futuro desejado no aqui e agora. É o "construir o novo mundo na casca do velho".', category: 'Justiça e Ativismo' },
  { id: 'term447', term: 'Alfabetização Corporal', definition: 'Capacidade de uma pessoa de ler, entender e responder aos sinais e necessidades de seu próprio corpo. Envolve interocepção, consciência emocional e respeito aos limites corporais.', category: 'Desenvolvimento Físico' },
  { id: 'term448', term: 'Justiça Epistêmica', definition: 'Campo que estuda a injustiça relacionada ao conhecimento. Inclui a "injustiça testemunhal" (quando o testemunho de alguém é desacreditado devido a preconceito) e a "injustiça hermenêutica" (quando um grupo não tem recursos para entender e expressar suas próprias experiências).', category: 'Teoria Crítica' },
  { id: 'term449', term: 'Capital Erótico', definition: 'Valor social que um indivíduo acumula como resultado de sua atratividade sexual. Padrões capacitistas, racistas e gordofóbicos determinam quem pode acumular esse capital.', category: 'Teoria Crítica' },
  { id: 'term450', term: 'Resiliência Comunitária', definition: 'A capacidade de uma comunidade inteira de resistir, se adaptar e se recuperar de adversidades, como desastres naturais ou crises econômicas. Baseia-se em redes de apoio mútuo e infraestrutura social.', category: 'Inclusão' },
  { id: 'term451', term: 'Trauma Complexo', definition: 'Resulta da exposição a eventos traumáticos múltiplos e prolongados, especialmente em um contexto interpessoal e durante o desenvolvimento. Leva a dificuldades mais amplas na regulação emocional, identidade e relacionamentos.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term452', term: 'Injustiça Alimentar', definition: 'A desigualdade no acesso a alimentos saudáveis, nutritivos e culturalmente apropriados, que afeta desproporcionalmente comunidades de baixa renda, racializadas e com deficiência.', category: 'Direitos' },
  { id: 'term453', term: 'Gênero-divergente', definition: 'Termo guarda-chuva para pessoas cuja identidade ou expressão de gênero difere das normas sociais de gênero, incluindo pessoas não-binárias, agênero, etc.', category: 'Linguagem' },
  { id: 'term454', term: 'Cuidado Informado pelo Trauma', definition: 'Abordagem de serviço que entende e responde ao impacto do trauma, enfatizando a segurança física e emocional e a oportunidade para a vítima reconstruir um senso de controle e empoderamento.', category: 'Intervenção' },
  { id: 'term455', term: 'Pesquisa-Ação Participativa', definition: 'Abordagem de pesquisa que envolve os membros da comunidade estudada como co-pesquisadores em todas as fases do processo, desde a definição do problema até a análise e disseminação dos resultados.', category: 'Modelos Teóricos' },
  { id: 'term456', term: 'Morte Digna', definition: 'O direito de uma pessoa de morrer com dignidade, controle e o mínimo de sofrimento possível. O debate sobre eutanásia e suicídio assistido é complexo e divide o movimento da deficiência, com temores de pressão e eugenia.', category: 'Direitos' },
  { id: 'term457', term: 'Etnografia Crítica', definition: 'Método de pesquisa qualitativa que não apenas descreve uma cultura ou grupo, mas também critica as condições de opressão e advoga pela mudança social.', category: 'Modelos Teóricos' },
  { id: 'term458', term: 'Mundanidade (Mundanity)', definition: 'O estudo das experiências e práticas ordinárias e cotidianas da vida. Para os estudos da deficiência, foca em como o capacitismo se manifesta nas pequenas interações e rotinas diárias.', category: 'Teoria Crítica' },
  { id: 'term459', term: 'Alegria Desafiadora', definition: 'Ato de cultivar e expressar alegria em meio a condições de opressão, não como uma negação do sofrimento, mas como uma prática de resistência e afirmação da vida.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term460', term: 'Vigilância Afetiva', definition: 'O trabalho emocional de monitorar e gerenciar as emoções dos outros para manter a paz ou garantir a segurança, frequentemente realizado de forma desproporcional por pessoas em posições subalternas.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term461', term: 'Direito à Memória e à Verdade', definition: 'O direito coletivo de conhecer a história de violações de direitos humanos, como as ocorridas em manicômios ou durante ditaduras, para garantir a não-repetição e a reparação.', category: 'Direitos' },
  { id: 'term462', term: 'Economia da Atenção', definition: 'Abordagem que trata a atenção humana como um recurso escasso a ser capturado e mercantilizado. A tecnologia é projetada para competir por nossa atenção, o que pode ser especialmente desafiador para pessoas com TDAH.', category: 'Ética e Tecnologia' },
  { id: 'term463', term: 'Diplomacia Cidadã', definition: 'Interações e intercâmbios entre cidadãos de diferentes países fora dos canais governamentais oficiais. Pode ser uma ferramenta para ativistas da deficiência construírem solidariedade global.', category: 'Justiça e Ativismo' },
  { id: 'term464', term: 'Intervenção Precoce Não-Normalizadora', definition: 'Abordagem de intervenção para crianças com deficiência que foca em dar-lhes ferramentas para navegar no mundo (comunicação, regulação), em vez de tentar fazê-las parecerem "normais" ou suprimir comportamentos autênticos.', category: 'Intervenção' },
  { id: 'term465', term: 'Luto Antecipatório', definition: 'Sentimento de luto que ocorre antes de uma perda real, comum em cuidadores de pessoas com doenças degenerativas ou em pessoas que recebem um diagnóstico terminal.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term466', term: 'Poliamor', definition: 'A prática ou desejo de ter relacionamentos íntimos com mais de uma pessoa, com o conhecimento e consentimento de todos os envolvidos. Desafia normas sobre relacionamento, cuidado e família.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term467', term: 'Anarquia Relacional', definition: 'Filosofia que aplica princípios anarquistas aos relacionamentos, rejeitando hierarquias impostas (ex: a ideia de que um parceiro romântico é mais importante que um amigo) e baseando cada relação em seus próprios termos de confiança e acordo mútuo.', category: 'Teoria Crítica' },
  { id: 'term468', term: 'Pensamento Lento', definition: 'Em contraste com o pensamento rápido e intuitivo, é o modo de pensamento deliberado, lógico e que exige esforço. Acessibilidade cognitiva envolve criar condições para que o pensamento lento possa ocorrer sem pressão.', category: 'Cognição' },
  { id: 'term469', term: 'Sonhos como Arquivo', definition: 'A ideia de que os sonhos podem ser um arquivo das experiências, medos e desejos de uma pessoa ou de uma comunidade, oferecendo insights sobre o impacto da opressão e as visões de libertação.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term470', term: 'Capacitismo Sistêmico', definition: 'O modo como o capacitismo está embutido nas leis, políticas, práticas institucionais e normas culturais da sociedade, criando desigualdades que vão além do preconceito individual.', category: 'Inclusão' },
  { id: 'term471', term: 'Comunicação Não-Violenta (CNV)', definition: 'Abordagem de comunicação desenvolvida por Marshall Rosenberg que foca em expressar honestamente e ouvir empaticamente, baseando-se em observações, sentimentos, necessidades e pedidos.', category: 'Comunicação' },
  { id: 'term472', term: 'Escrita Terapêutica', definition: 'O uso da escrita como uma ferramenta para explorar pensamentos e sentimentos, processar experiências difíceis e promover a autoconsciência e a cura.', category: 'Intervenção' },
  { id: 'term473', term: 'Geração Z e Ativismo Digital', definition: 'A forma como a geração mais jovem utiliza as plataformas digitais para organizar protestos, disseminar informação e criar cultura em torno da justiça social, com uma linguagem e estética próprias.', category: 'Justiça e Ativismo' },
  { id: 'term474', term: 'Teoria Queer do Tempo', definition: 'Argumenta que as trajetórias de vida de pessoas LGBTQIA+ frequentemente não seguem a linha do tempo "normal" (escola, casamento, filhos, aposentadoria), propondo outras formas de marcar e valorizar o tempo e a vida.', category: 'Teoria Crítica' },
  { id: 'term475', term: 'Racismo Ambiental', definition: 'A forma como comunidades de baixa renda e racializadas são desproporcionalmente expostas a poluição, lixo tóxico e outros riscos ambientais, o que leva a maiores taxas de doenças e deficiências.', category: 'Perspectivas Interseccionais' },
  { id: 'term476', term: 'Auditoria de Acessibilidade', definition: 'Uma avaliação sistemática de um local, serviço ou produto digital para determinar seu nível de conformidade com os padrões de acessibilidade.', category: 'Tecnologia e Suporte' },
  { id: 'term477', term: 'Capitalismo Afetivo', definition: 'A mercantilização das emoções e dos relacionamentos, onde sentimentos como autenticidade e conexão são usados para vender produtos e gerenciar trabalhadores. Critica como o cuidado genuíno é transformado em "trabalho emocional" pago.', category: 'Teoria Crítica' },
  { id: 'term478', term: 'Decrescimento', definition: 'Movimento que defende uma redução planejada e equitativa da produção e do consumo nos países ricos para alcançar a sustentabilidade ambiental e o bem-estar social, desafiando a lógica do crescimento infinito.', category: 'Modelos Teóricos' },
  { id: 'term479', term: 'Memória Muscular', definition: 'A capacidade de reproduzir um movimento motor específico com precisão e eficiência devido à repetição, permitindo que a tarefa se torne automática.', category: 'Desenvolvimento Físico' },
  { id: 'term480', term: 'Justiça do Sono', definition: 'Luta pelo direito de todas as pessoas a um sono adequado e reparador, reconhecendo que a poluição sonora, as más condições de moradia, o estresse e os horários de trabalho exploratórios afetam desproporcionalmente os grupos marginalizados.', category: 'Direitos' },
  { id: 'term481', term: 'Etnomatemática', definition: 'Campo de estudo que investiga as diferentes formas como a matemática é praticada e compreendida em diferentes grupos culturais, desafiando a ideia de que a matemática ocidental é a única forma válida.', category: 'Educação Inclusiva' },
  { id: 'term482', term: 'Carga Crônica de Estresse', definition: 'A exposição contínua e prolongada a estressores, como discriminação e pobreza, que leva a um desgaste fisiológico e aumenta o risco de doenças crônicas.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term483', term: 'Letramento Crítico de Dados', definition: 'Habilidade de entender, questionar e usar dados de forma crítica, reconhecendo como eles são coletados, interpretados e apresentados, e quais vieses podem conter.', category: 'Aprendizagem' },
  { id: 'term484', term: 'Militância de Papel Passado', definition: 'Crítica a atitudes de pessoas que, por terem algum conhecimento teórico ou um histórico de ativismo, se sentem no direito de policiar ou deslegitimar as experiências e lutas de outras pessoas, especialmente as mais novas no movimento.', category: 'Justiça e Ativismo' },
  { id: 'term485', term: 'Otimização para Mecanismos de Busca (SEO) Acessível', definition: 'Práticas de SEO que melhoram a visibilidade de um site sem comprometer sua acessibilidade, garantindo que o conteúdo seja encontrável e utilizável por todos.', category: 'Tecnologia e Suporte' },
  { id: 'term486', term: 'Performance de Aliado (Performative Allyship)', definition: 'A prática de se mostrar como um aliado de um grupo marginalizado para obter capital social (curtidas, aprovação), sem se engajar em ações concretas que desafiem as estruturas de opressão.', category: 'Inclusão' },
  { id: 'term487', term: 'Tecnologia Convincente (Persuasive Tech)', definition: 'Design de tecnologia que visa influenciar as atitudes e comportamentos dos usuários, muitas vezes de forma sutil ou exploratória, como o scroll infinito em redes sociais.', category: 'Ética e Tecnologia' },
  { id: 'term488', term: 'Corpo Político', definition: 'A ideia de que nossos corpos não são apenas entidades biológicas, mas são moldados por relações de poder, inscritos com significados sociais e se tornam locais de resistência e controle político.', category: 'Teoria Crítica' },
  { id: 'term489', term: 'Cultura Regenerativa', definition: 'Cultura que busca criar sistemas sociais e ecológicos saudáveis e resilientes, focando na cura, na cooperação e no cuidado mútuo, como uma alternativa a culturas extrativistas e degenerativas.', category: 'Modelos Teóricos' },
  { id: 'term490', term: 'Solidão Crônica', definition: 'A experiência prolongada e angustiante de se sentir solitário ou socialmente isolado. É uma questão de saúde pública que afeta desproporcionalmente pessoas com deficiência devido a barreiras sociais e atitudinais.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term491', term: 'Consentimento Informado em Pesquisa', definition: 'Processo ético que garante que os participantes de uma pesquisa compreendam todos os aspectos do estudo (riscos, benefícios, procedimentos) e concordem voluntariamente em participar. Requer formatos acessíveis para ser verdadeiramente inclusivo.', category: 'Ética e Tecnologia' },
  { id: 'term492', term: 'Design Universal vs. Design Inclusivo', definition: 'Enquanto o Design Universal visa uma única solução que funcione para o maior número de pessoas, o Design Inclusivo reconhece que não há solução única e foca em oferecer múltiplas formas de participação, valorizando a diversidade de necessidades.', category: 'Inclusão' },
  { id: 'term493', term: 'Futurismo Crip', definition: 'Subgênero da ficção científica e do ativismo que imagina futuros onde a deficiência é central, não como algo a ser erradicado, mas como uma fonte de identidade, tecnologia e novas formas de ser.', category: 'Teoria Crítica' },
  { id: 'term494', term: 'Mapeamento Afetivo', definition: 'Prática de mapear as emoções e os sentimentos associados a um determinado lugar ou território, revelando as geografias invisíveis de alegria, medo, pertencimento ou exclusão.', category: 'Desenvolvimento Socioafetivo' },
  { id: 'term495', term: 'Cultura do Litígio', definition: 'Tendência de resolver disputas através do sistema legal. No contexto da deficiência, o litígio estratégico pode ser uma ferramenta poderosa para garantir direitos, mas também pode ser lento e inacessível.', category: 'Direitos' },
  { id: 'term496', term: 'Neoliberalismo', definition: 'Ideologia política e econômica que favorece a desregulamentação do mercado, a privatização de serviços públicos e a responsabilidade individual. Impacta negativamente as pessoas com deficiência ao cortar redes de segurança social e promover a competição.', category: 'Teoria Crítica' },
  { id: 'term497', term: 'Dívida Histórica', definition: 'O reconhecimento de que as desigualdades atuais são resultado de injustiças históricas, como a escravidão e o colonialismo, e que as nações e instituições que se beneficiaram disso têm uma dívida a ser paga através de reparações e políticas de equidade.', category: 'Justiça e Ativismo' },
  { id: 'term498', term: 'Amor-próprio Político', definition: 'A prática do amor-próprio não como um ato individualista de bem-estar, mas como um ato político de resistência para pessoas cujos corpos e mentes foram desvalorizados e atacados pela sociedade.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term499', term: 'Cansaço de Batalha (Battle Fatigue)', definition: 'O esgotamento mental e emocional profundo que resulta de lutar constantemente contra a opressão sistêmica no dia a dia.', category: 'Saúde Mental e Desenvolvimento' },
  { id: 'term500', term: 'Ubuntu', definition: 'Filosofia humanista africana que pode ser traduzida como "Eu sou porque nós somos". Enfatiza a interconexão, a comunidade e a humanidade compartilhada, oferecendo uma poderosa alternativa ao individualismo ocidental e um fundamento para o cuidado coletivo.', category: 'Modelos Teóricos' }
];

